286→    private static func importImage(
287→        item: PickerWorkItem,
288→        key: VaultKey,
289→        encryptionKey: Data,
290→        optimizationMode: MediaOptimizer.Mode
291→    ) async throws -> VaultFileItem? {
292→        guard !Task.isCancelled else { return nil }
293→
294→        // Load image via file representation to preserve EXIF data for original date extraction
295→        let tempImageURL = try await loadImageURL(from: item.provider)
296→        defer { try? FileManager.default.removeItem(at: tempImageURL) }
297→
298→        // Extract EXIF creation date before any conversion
299→        let originalDate = FileUtilities.extractImageCreationDate(from: tempImageURL)
300→
301→        guard !Task.isCancelled else { return nil }
302→
303→        // Pass source file directly to MediaOptimizer — it uses CGImageSource which handles
304→        // any image format natively. Avoids the old UIImage → jpegData → disk → re-decode path
305→        // that doubled CPU work and peak memory per image.
306→        // Fall back to image/jpeg for uncommon formats (WebP, TIFF, BMP, DNG, etc.) where
307→        // FileUtilities returns application/octet-stream — we know it's an image from PHPicker.
308→        let detectedMime = FileUtilities.mimeType(forExtension: tempImageURL.pathExtension)
309→        let sourceMimeType = detectedMime.hasPrefix("image/") ? detectedMime : "image/jpeg"
310→        let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
311→            fileURL: tempImageURL, mimeType: sourceMimeType, mode: optimizationMode
312→        )
313→        defer {
314→            if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
315→        }
316→
317→        let ext: String
318→        if mimeType == "image/heic" {
319→            ext = "heic"
320→        } else {
321→            let sourceExt = tempImageURL.pathExtension.lowercased()
322→            ext = sourceExt.isEmpty ? "jpg" : sourceExt
323→        }
324→        let filename = "IMG_\(Date().timeIntervalSince1970)_\(item.originalIndex).\(ext)"
325→
326→        // autoreleasepool releases CGImage/UIImage temporaries from thumbnail generation
327→        // before the async storeFileFromURL call, preventing memory accumulation across workers
328→        let thumbnail: Data? = autoreleasepool {
329→            FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
330→        }
331→        let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
332→
333→        let fileId = try await VaultStorage.shared.storeFileFromURL(
334→            optimizedURL, filename: filename, mimeType: mimeType,
335→            with: key, thumbnailData: thumbnail, originalDate: originalDate
336→        )
337→
338→        let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
339→        if let encThumb {
340→            await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
341→        }
342→
343→        return VaultFileItem(
344→            id: fileId, size: fileSize,
345→            hasThumbnail: encThumb != nil, mimeType: mimeType,
346→            filename: filename, createdAt: Date(), originalDate: originalDate
347→        )
348→    }
