350→    private static func importFileFromURL(
351→        url: URL,
352→        key: VaultKey,
353→        encryptionKey: Data,
354→        optimizationMode: MediaOptimizer.Mode
355→    ) async throws -> VaultFileItem? {
356→        guard !Task.isCancelled else { return nil }
