426→    // MARK: - Utilities (nonisolated — no MainActor dependency)
427→
428→    /// Loads an image from PHPicker provider to a temp URL, preserving EXIF metadata.
429→    private static func loadImageURL(from provider: NSItemProvider) async throws -> URL {
430→        try await withCheckedThrowingContinuation { continuation in
431→            provider.loadFileRepresentation(forTypeIdentifier: UTType.image.identifier) { url, error in
432→                if let error {
433→                    continuation.resume(throwing: error)
434→                    return
435→                }
436→                guard let url else {
437→                    continuation.resume(throwing: CocoaError(.fileReadUnknown))
438→                    return
439→                }
440→                let destURL = FileManager.default.temporaryDirectory
441→                    .appendingPathComponent(UUID().uuidString)
442→                    .appendingPathExtension(url.pathExtension.isEmpty ? "jpg" : url.pathExtension)
443→                do {
444→                    try FileManager.default.copyItem(at: url, to: destURL)
445→                    continuation.resume(returning: destURL)
446→                } catch {
447→                    continuation.resume(throwing: error)
448→                }
449→            }
450→        }
451→    }
