455→        try await withCheckedThrowingContinuation { continuation in
456→            provider.loadFileRepresentation(forTypeIdentifier: UTType.movie.identifier) { url, error in
457→                if let error {
458→                    continuation.resume(throwing: error)
459→                    return
460→                }
461→                guard let url else {
462→                    continuation.resume(throwing: CocoaError(.fileReadUnknown))
463→                    return
464→                }
