478→    /// Generates thumbnail + duration + creation date from a video URL.
479→    private static func generateVideoMetadata(from url: URL) async -> (thumbnail: Data?, duration: TimeInterval?, creationDate: Date?) {
480→        let asset = AVAsset(url: url)
481→        let generator = AVAssetImageGenerator(asset: asset)
482→        generator.appliesPreferredTrackTransform = true
483→        generator.maximumSize = CGSize(width: 400, height: 400)
484→
485→        var thumbnail: Data?
486→        var duration: TimeInterval?
487→        var creationDate: Date?
488→
489→        if let cmDuration = try? await asset.load(.duration) {
490→            let seconds = CMTimeGetSeconds(cmDuration)
491→            if seconds.isFinite && seconds > 0 { duration = seconds }
492→        }
493→
494→        // Extract creation date from video metadata
