import AVFoundation
import CoreMedia
import ImageIO
import UniformTypeIdentifiers
import UIKit
import os.log

private let optimizerLogger = Logger(subsystem: "app.vaultaire.ios", category: "MediaOptimizer")

/// Optimizes media files before vault storage using modern codecs.
/// Images → HEIC (ImageIO), Videos → HEVC 1080p (AVAssetReader/AVAssetWriter with bitrate control).
/// Non-media files pass through unchanged.
///
/// Not an actor — all methods operate on temp files with no shared mutable state.
/// Actor isolation was serializing parallel import workers through a single executor,
/// defeating the 4-worker concurrency design in ParallelImporter.
final class MediaOptimizer: Sendable {
    static let shared = MediaOptimizer()

    enum Mode: String, Sendable {
        case optimized
        case original
    }

    /// Returns (outputURL, outputMimeType, wasOptimized).
    /// If `wasOptimized` is true, caller must delete the temp file after use.
    func optimize(fileURL: URL, mimeType: String, mode: Mode) async throws -> (URL, String, Bool) {
        guard mode == .optimized else {
            return (fileURL, mimeType, false)
        }

        if mimeType.hasPrefix("image/") {
            return try optimizeImage(fileURL: fileURL, mimeType: mimeType)
        } else if mimeType.hasPrefix("video/") {
            return try await optimizeVideo(fileURL: fileURL, mimeType: mimeType)
        }

        // Non-media: passthrough
        return (fileURL, mimeType, false)
    }

    /// Optimizes a UIImage (e.g., from camera) to HEIC temp file.
    /// Returns (tempFileURL, mimeType). Caller must delete temp file.
    func optimizeImage(_ image: UIImage, mode: Mode) async throws -> (URL, String) {
        if mode == .original {
            // Write as JPEG to temp file (avoids holding jpegData in memory alongside UIImage)
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
                .appendingPathExtension("jpg")
            guard let data = image.jpegData(compressionQuality: 0.8) else {
                throw OptimizationError.imageConversionFailed
            }
            try data.write(to: tempURL, options: [.atomic])
            return (tempURL, "image/jpeg")
        }

        // Write HEIC via ImageIO — avoids large Data intermediary
        guard let cgImage = image.cgImage else {
            throw OptimizationError.imageConversionFailed
        }

        let tempURL = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("heic")

        guard let destination = CGImageDestinationCreateWithURL(
            tempURL as CFURL,
            AVFileType.heic as CFString,
            1,
            nil
        ) else {
            throw OptimizationError.heicEncodingFailed
        }

        // Check if image has alpha to optimize file size and memory
        let hasAlpha = cgImage.alphaInfo != .none && cgImage.alphaInfo != .noneSkipFirst && cgImage.alphaInfo != .noneSkipLast
        let options: [CFString: Any] = [
            kCGImageDestinationLossyCompressionQuality: 0.6,
            kCGImagePropertyHasAlpha: hasAlpha
        ]
        CGImageDestinationAddImage(destination, cgImage, options as CFDictionary)

        guard CGImageDestinationFinalize(destination) else {
            FileUtilities.cleanupTemporaryFile(at: tempURL)
            throw OptimizationError.heicEncodingFailed
        }

        let fileSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0
        optimizerLogger.info("Camera image → HEIC: \(fileSize) bytes")

        return (tempURL, "image/heic")
    }

    // MARK: - Image Optimization

    private func optimizeImage(fileURL: URL, mimeType: String) throws -> (URL, String, Bool) {
        // Skip if already HEIC
        if mimeType == "image/heic" || mimeType == "image/heif" {
            return (fileURL, mimeType, false)
        }

        // autoreleasepool ensures CGImageSource, CGImage, and CGImageDestination temporaries
        // are released immediately. With 4 parallel workers, this prevents ~100-200MB of
        // accumulated ObjC objects between executor yields.
        return try autoreleasepool {
            guard let source = CGImageSourceCreateWithURL(fileURL as CFURL, nil) else {
                return (fileURL, mimeType, false)
            }

            // Check image dimensions for potential downsampling
            let properties = CGImageSourceCopyPropertiesAtIndex(source, 0, nil) as? [CFString: Any]
            let width = properties?[kCGImagePropertyPixelWidth] as? Int ?? 0
            let height = properties?[kCGImagePropertyPixelHeight] as? Int ?? 0
            let maxDimension = max(width, height)

            // Get source image — downsample if > 4096px
            // Read EXIF orientation to preserve it when writing output
            let orientation: CGImagePropertyOrientation
            let cgImage: CGImage
            if maxDimension > 4096 {
                let thumbOptions: [CFString: Any] = [
                    kCGImageSourceCreateThumbnailFromImageAlways: true,
                    kCGImageSourceCreateThumbnailWithTransform: true,
                    kCGImageSourceThumbnailMaxPixelSize: 4096
                ]
                guard let thumb = CGImageSourceCreateThumbnailAtIndex(source, 0, thumbOptions as CFDictionary) else {
                    return (fileURL, mimeType, false)
                }
                cgImage = thumb
                // kCGImageSourceCreateThumbnailWithTransform already rotated the pixels,
                // so mark as .up to avoid double-rotation in the output HEIC.
                orientation = .up
            } else {
                guard let full = CGImageSourceCreateImageAtIndex(source, 0, nil) else {
                    return (fileURL, mimeType, false)
                }
                cgImage = full
                // Full-size path: pixels are unrotated, so preserve original EXIF orientation.
                orientation = (properties?[kCGImagePropertyOrientation] as? UInt32).flatMap { CGImagePropertyOrientation(rawValue: $0) } ?? .up
            }

            // Write HEIC to temp file
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
                .appendingPathExtension("heic")

            guard let destination = CGImageDestinationCreateWithURL(
                tempURL as CFURL,
                AVFileType.heic as CFString,
                1,
                nil
            ) else {
                return (fileURL, mimeType, false)
            }

            // Preserve EXIF orientation metadata when writing the output image
            // Check if image has alpha to optimize file size and memory
            let hasAlpha = cgImage.alphaInfo != .none && cgImage.alphaInfo != .noneSkipFirst && cgImage.alphaInfo != .noneSkipLast
            let options: [CFString: Any] = [
                kCGImageDestinationLossyCompressionQuality: 0.6,
                kCGImagePropertyOrientation: orientation.rawValue,
                kCGImagePropertyHasAlpha: hasAlpha
            ]
            CGImageDestinationAddImage(destination, cgImage, options as CFDictionary)

            guard CGImageDestinationFinalize(destination) else {
                FileUtilities.cleanupTemporaryFile(at: tempURL)
                return (fileURL, mimeType, false)
            }

            let originalSize = (try? FileManager.default.attributesOfItem(atPath: fileURL.path)[.size] as? Int64) ?? 0
            let optimizedSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0
            optimizerLogger.info("Image optimized: \(originalSize) → \(optimizedSize) bytes (\(originalSize > 0 ? Int(100 - (optimizedSize * 100 / originalSize)) : 0)% reduction)")

            return (tempURL, "image/heic", true)
        }
    }

    // MARK: - Video Optimization

    /// Target bitrate tiers for HEVC encoding (bits per second).
    /// These deliver excellent mobile viewing quality at dramatically lower file sizes.
    private static let bitrate1080p = 4_000_000  // 4 Mbps — comparable to streaming HD
    private static let bitrate720p  = 2_500_000  // 2.5 Mbps
    private static let bitrateSD    = 1_500_000  // 1.5 Mbps
    private static let audioBitrate = 128_000    // 128 kbps AAC

    private func optimizeVideo(fileURL: URL, mimeType: String) async throws -> (URL, String, Bool) {
        let asset = AVURLAsset(url: fileURL)

        // Check if already compact enough — skip if bitrate is already low
        if await isAlreadyOptimalVideo(asset) {
            optimizerLogger.info("Video already optimal, skipping")
            return (fileURL, mimeType, false)
        }

        guard let videoTrack = try? await asset.loadTracks(withMediaType: .video).first else {
            return (fileURL, mimeType, false)
        }

        let naturalSize = try await videoTrack.load(.naturalSize)
        let preferredTransform = try await videoTrack.load(.preferredTransform)

        // Calculate output dimensions (max 1080p, preserve aspect ratio)
        let targetSize = Self.targetVideoSize(from: naturalSize, transform: preferredTransform)
        let targetBitrate = Self.targetBitrate(for: targetSize)

        optimizerLogger.info("Video transcode: \(Int(naturalSize.width))x\(Int(naturalSize.height)) → \(targetSize.width)x\(targetSize.height) @ \(targetBitrate / 1000) kbps")

        let tempURL = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("mp4")

        do {
            // Set up reader
            let reader = try AVAssetReader(asset: asset)

            let videoReaderOutput = AVAssetReaderTrackOutput(track: videoTrack, outputSettings: [
                kCVPixelBufferPixelFormatTypeKey as String: REDACTED
            ])
            videoReaderOutput.alwaysCopiesSampleData = false
            reader.add(videoReaderOutput)

            // Set up writer
            let writer = try AVAssetWriter(outputURL: tempURL, fileType: .mp4)

            let videoWriterInput = AVAssetWriterInput(mediaType: .video, outputSettings: [
                AVVideoCodecKey: AVVideoCodecType.hevc,
                AVVideoWidthKey: targetSize.width,
                AVVideoHeightKey: targetSize.height,
                AVVideoCompressionPropertiesKey: [
                    AVVideoAverageBitRateKey: targetBitrate,
                    AVVideoExpectedSourceFrameRateKey: 30,
                    AVVideoMaxKeyFrameIntervalKey: 60
                ]
            ])
            videoWriterInput.expectsMediaDataInRealTime = false
            videoWriterInput.transform = preferredTransform
            writer.add(videoWriterInput)

            // Audio track (if present)
            var audioReaderOutput: AVAssetReaderTrackOutput?
            var audioWriterInput: AVAssetWriterInput?
            if let audioTrack = try? await asset.loadTracks(withMediaType: .audio).first {
                let audioOutput = AVAssetReaderTrackOutput(track: audioTrack, outputSettings: [
                    AVFormatIDKey: kAudioFormatLinearPCM,
                    AVLinearPCMBitDepthKey: 16,
                    AVLinearPCMIsBigEndianKey: false,
                    AVLinearPCMIsFloatKey: false,
                    AVLinearPCMIsNonInterleaved: false
                ])
                audioOutput.alwaysCopiesSampleData = false
                reader.add(audioOutput)
                audioReaderOutput = audioOutput

                let audioInput = AVAssetWriterInput(mediaType: .audio, outputSettings: [
                    AVFormatIDKey: kAudioFormatMPEG4AAC,
                    AVEncoderBitRateKey: Self.audioBitrate,
                    AVSampleRateKey: 44100,
                    AVNumberOfChannelsKey: 2
                ])
                audioInput.expectsMediaDataInRealTime = false
                writer.add(audioInput)
                audioWriterInput = audioInput
            }

            // Start reading/writing
            reader.startReading()
            writer.startWriting()
            writer.startSession(atSourceTime: .zero)

            // Process video and audio tracks concurrently.
            // Per-transcode queues so parallel video workers don't serialize on shared queues.
            let id = UUID().uuidString.prefix(8)
            let videoXferQueue = DispatchQueue(label: "app.vaultaire.transcode.video.\(id)")
            let audioXferQueue = DispatchQueue(label: "app.vaultaire.transcode.audio.\(id)")
            await withTaskGroup(of: Void.self) { group in
                group.addTask {
                    await Self.transferSamples(from: videoReaderOutput, to: videoWriterInput, on: videoXferQueue)
                }
                if let audioOutput = audioReaderOutput, let audioInput = audioWriterInput {
                    group.addTask {
                        await Self.transferSamples(from: audioOutput, to: audioInput, on: audioXferQueue)
                    }
                }
            }

            await writer.finishWriting()

            guard writer.status == .completed else {
                try? FileManager.default.removeItem(at: tempURL)
                let errorDesc = writer.error?.localizedDescription ?? "unknown"
                optimizerLogger.error("Video writer failed: \(errorDesc, privacy: .public)")
                return (fileURL, mimeType, false)
            }

            reader.cancelReading()
        } catch {
            try? FileManager.default.removeItem(at: tempURL)
            optimizerLogger.error("Video transcode setup failed: \(error.localizedDescription, privacy: .public)")
            return (fileURL, mimeType, false)
        }

        let originalSize = (try? FileManager.default.attributesOfItem(atPath: fileURL.path)[.size] as? Int64) ?? 0
        let optimizedSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0

        // If optimized file isn't meaningfully smaller (< 10% reduction), skip it
        if originalSize > 0 && optimizedSize >= Int64(Double(originalSize) * 0.9) {
            try? FileManager.default.removeItem(at: tempURL)
            optimizerLogger.info("Video transcode yielded < 10% reduction (\(originalSize) → \(optimizedSize)), keeping original")
            return (fileURL, mimeType, false)
        }

        let reduction = originalSize > 0 ? Int(100 - (optimizedSize * 100 / originalSize)) : 0
        optimizerLogger.info("Video optimized: \(originalSize) → \(optimizedSize) bytes (\(reduction)% reduction)")

        return (tempURL, "video/mp4", true)
    }

    /// Transfer sample buffers from reader output to writer input using the proper callback pattern.
    /// Each transcode creates its own serial queue so parallel video workers don't serialize.
    private static func transferSamples(from output: AVAssetReaderTrackOutput, to input: AVAssetWriterInput, on queue: DispatchQueue) async {
        await withCheckedContinuation { (continuation: CheckedContinuation<Void, Never>) in
            // AVAssetWriterInput/AVAssetReaderTrackOutput are thread-safe for this callback pattern.
            // The requestMediaDataWhenReady callback is always called on the provided queue serially.
            nonisolated(unsafe) let output = output
            nonisolated(unsafe) let input = input
            var resumed = false
            input.requestMediaDataWhenReady(on: queue) {
                while input.isReadyForMoreMediaData {
                    guard let buffer = output.copyNextSampleBuffer() else {
                        input.markAsFinished()
                        if !resumed {
                            resumed = true
                            continuation.resume()
                        }
                        return
                    }
                    input.append(buffer)
                }
            }
        }
    }

    /// Calculate output video dimensions: cap at 1080p, preserve aspect ratio.
    /// Uses naturalSize directly — the writer's transform property handles orientation.
    private static func targetVideoSize(from naturalSize: CGSize, transform _: CGAffineTransform) -> (width: Int, height: Int) {
        // Use naturalSize directly — pixel buffers are in natural orientation,
        // and videoWriterInput.transform handles display rotation.
        let w = naturalSize.width
        let h = naturalSize.height
        let maxDim = max(w, h)

        if maxDim <= 1920 {
            // Already ≤ 1080p — keep original dimensions (rounded to even)
            return (Int(w / 2) * 2, Int(h / 2) * 2)
        }

        let scale = 1920.0 / maxDim
        // Round to nearest even number (required by video encoder)
        let newW = Int((w * scale) / 2) * 2
        let newH = Int((h * scale) / 2) * 2
        return (max(newW, 2), max(newH, 2))
    }

    /// Select target bitrate based on output resolution.
    private static func targetBitrate(for size: (width: Int, height: Int)) -> Int {
        let maxDim = max(size.width, size.height)
        if maxDim >= 1080 { return bitrate1080p }
        if maxDim >= 720 { return bitrate720p }
        return bitrateSD
    }

    /// Check if video is already compact enough to skip optimization.
    /// Skips if HEVC, ≤ 1080p, AND estimated bitrate is already ≤ 5 Mbps.
    private func isAlreadyOptimalVideo(_ asset: AVURLAsset) async -> Bool {
        guard let videoTrack = try? await asset.loadTracks(withMediaType: .video).first else {
            return false
        }

        // Check codec
        guard let formatDescriptions = try? await videoTrack.load(.formatDescriptions),
              let desc = formatDescriptions.first else {
            return false
        }

        let mediaSubType = CMFormatDescriptionGetMediaSubType(desc)
        // 'hvc1' FourCC = HEVC
        let isHEVC = mediaSubType == 0x68766331

        guard isHEVC else { return false }

        // Check resolution
        guard let naturalSize = try? await videoTrack.load(.naturalSize) else {
            return false
        }
        let maxDimension = max(naturalSize.width, naturalSize.height)
        guard maxDimension <= 1920 else { return false }

        // Check bitrate — only skip if already low enough
        guard let estimatedRate = try? await videoTrack.load(.estimatedDataRate) else {
            return false
        }
        let isLowBitrate = estimatedRate <= 5_000_000 // 5 Mbps threshold

        if !isLowBitrate {
            optimizerLogger.info("HEVC ≤1080p but bitrate \(Int(estimatedRate / 1000)) kbps exceeds threshold, will re-encode")
        }

        return isLowBitrate
    }

    // MARK: - Errors

    enum OptimizationError: LocalizedError {
        case imageConversionFailed
        case heicEncodingFailed

        var errorDescription: String? {
            if self == .imageConversionFailed { return "Could not process image for optimization" }
            return "HEIC encoding failed"
        }
    }
}

// MARK: - Filename Helpers

extension MediaOptimizer {
    /// Updates filename extension to match the optimized format.
    static func updatedFilename(_ filename: String, newMimeType: String) -> String {
        let ext: String
        switch newMimeType {
        case "image/heic", "image/heif": ext = "heic"
        case "video/mp4": ext = "mp4"
        default: return filename
        }

        let name = (filename as NSString).deletingPathExtension
        return "\(name).\(ext)"
    }
}

import XCTest
import AVFoundation
@testable import Vault

final class MediaOptimizerTests: XCTestCase {

    // MARK: - Helpers

    /// Creates a minimal valid JPEG file at a temp URL.
    private func createTempJPEG(size: CGSize = CGSize(width: 100, height: 100)) throws -> URL {
        let renderer = UIGraphicsImageRenderer(size: size)
        let image = renderer.image { ctx in
            UIColor.blue.setFill()
            ctx.fill(CGRect(origin: .zero, size: size))
        }
        guard let data = image.jpegData(compressionQuality: 0.9) else {
            throw NSError(domain: "Test", code: 1, userInfo: [NSLocalizedDescriptionKey: "Failed to create JPEG"])
        }
        let url = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("jpg")
        try data.write(to: url)
        return url
    }

    /// Creates a minimal valid PNG file at a temp URL.
    private func createTempPNG(size: CGSize = CGSize(width: 100, height: 100)) throws -> URL {
        let renderer = UIGraphicsImageRenderer(size: size)
        let image = renderer.image { ctx in
            UIColor.green.setFill()
            ctx.fill(CGRect(origin: .zero, size: size))
        }
        guard let data = image.pngData() else {
            throw NSError(domain: "Test", code: 1, userInfo: [NSLocalizedDescriptionKey: "Failed to create PNG"])
        }
        let url = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("png")
        try data.write(to: url)
        return url
    }

    /// Creates a temp file with arbitrary data (non-media).
    private func createTempFile(ext: String, data: Data) throws -> URL {
        let url = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension(ext)
        try data.write(to: url)
        return url
    }

    // MARK: - Mode Tests

    func testOriginalModePassthroughForImage() async throws {
        let jpegURL = try createTempJPEG()
        defer { try? FileManager.default.removeItem(at: jpegURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: jpegURL, mimeType: "image/jpeg", mode: .original
        )

        XCTAssertFalse(wasOptimized, "Original mode should not optimize")
        XCTAssertEqual(mimeType, "image/jpeg")
        XCTAssertEqual(outputURL, jpegURL, "Should return same URL in original mode")
    }

    func testOriginalModePassthroughForVideo() async throws {
        // Create a minimal video to test passthrough
        let videoURL = try await createMinimalVideo()
        defer { try? FileManager.default.removeItem(at: videoURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: videoURL, mimeType: "video/quicktime", mode: .original
        )

        XCTAssertFalse(wasOptimized)
        XCTAssertEqual(mimeType, "video/quicktime")
        XCTAssertEqual(outputURL, videoURL)
    }

    // MARK: - Image Optimization

    func testJPEGOptimizesToHEIC() async throws {
        let jpegURL = try createTempJPEG(size: CGSize(width: 500, height: 500))
        defer { try? FileManager.default.removeItem(at: jpegURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: jpegURL, mimeType: "image/jpeg", mode: .optimized
        )
        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        XCTAssertTrue(wasOptimized, "JPEG should be optimized to HEIC")
        XCTAssertEqual(mimeType, "image/heic")
        XCTAssertTrue(outputURL.pathExtension == "heic")
        XCTAssertTrue(FileManager.default.fileExists(atPath: outputURL.path))
    }

    func testPNGOptimizesToHEIC() async throws {
        let pngURL = try createTempPNG(size: CGSize(width: 500, height: 500))
        defer { try? FileManager.default.removeItem(at: pngURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: pngURL, mimeType: "image/png", mode: .optimized
        )
        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        XCTAssertTrue(wasOptimized)
        XCTAssertEqual(mimeType, "image/heic")
    }

    func testHEICImageSkipsOptimization() async throws {
        // Create a JPEG, optimize it to HEIC, then try to optimize the HEIC
        let jpegURL = try createTempJPEG()
        defer { try? FileManager.default.removeItem(at: jpegURL) }

        let (heicURL, _, firstWasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: jpegURL, mimeType: "image/jpeg", mode: .optimized
        )
        XCTAssertTrue(firstWasOptimized)
        defer { try? FileManager.default.removeItem(at: heicURL) }

        // Now try to optimize the HEIC — should skip
        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: heicURL, mimeType: "image/heic", mode: .optimized
        )

        XCTAssertFalse(wasOptimized, "HEIC should not be re-optimized")
        XCTAssertEqual(mimeType, "image/heic")
        XCTAssertEqual(outputURL, heicURL)
    }

    func testHEIFImageSkipsOptimization() async throws {
        let jpegURL = try createTempJPEG()
        defer { try? FileManager.default.removeItem(at: jpegURL) }

        let (_, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: jpegURL, mimeType: "image/heif", mode: .optimized
        )

        XCTAssertFalse(wasOptimized, "HEIF should not be re-optimized")
        XCTAssertEqual(mimeType, "image/heif")
    }

    func testOptimizedImageIsSmallerThanOriginal() async throws {
        // Create a larger image for more noticeable compression
        let jpegURL = try createTempJPEG(size: CGSize(width: 1000, height: 1000))
        defer { try? FileManager.default.removeItem(at: jpegURL) }
        let originalSize = try FileManager.default.attributesOfItem(atPath: jpegURL.path)[.size] as? Int64 ?? 0

        let (outputURL, _, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: jpegURL, mimeType: "image/jpeg", mode: .optimized
        )
        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        XCTAssertTrue(wasOptimized)
        let optimizedSize = try FileManager.default.attributesOfItem(atPath: outputURL.path)[.size] as? Int64 ?? 0
        XCTAssertLessThan(optimizedSize, originalSize, "HEIC output should be smaller than JPEG input")
    }

    // MARK: - Non-Media Passthrough

    func testPDFPassesThrough() async throws {
        let pdfURL = try createTempFile(ext: "pdf", data: Data(repeating: 0x25, count: 1000))
        defer { try? FileManager.default.removeItem(at: pdfURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: pdfURL, mimeType: "application/pdf", mode: .optimized
        )

        XCTAssertFalse(wasOptimized)
        XCTAssertEqual(mimeType, "application/pdf")
        XCTAssertEqual(outputURL, pdfURL)
    }

    func testZIPPassesThrough() async throws {
        let zipURL = try createTempFile(ext: "zip", data: Data(repeating: 0x50, count: 500))
        defer { try? FileManager.default.removeItem(at: zipURL) }

        let (_, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: zipURL, mimeType: "application/zip", mode: .optimized
        )

        XCTAssertFalse(wasOptimized)
        XCTAssertEqual(mimeType, "application/zip")
    }

    func testPlainTextPassesThrough() async throws {
        let txtURL = try createTempFile(ext: "txt", data: "Hello world".data(using: .utf8)!)
        defer { try? FileManager.default.removeItem(at: txtURL) }

        let (_, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: txtURL, mimeType: "text/plain", mode: .optimized
        )

        XCTAssertFalse(wasOptimized)
        XCTAssertEqual(mimeType, "text/plain")
    }

    // MARK: - UIImage Optimization

    func testOptimizeUIImageOptimizedMode() async throws {
        let renderer = UIGraphicsImageRenderer(size: CGSize(width: 200, height: 200))
        let image = renderer.image { ctx in
            UIColor.red.setFill()
            ctx.fill(CGRect(x: 0, y: 0, width: 200, height: 200))
        }

        let (url, mimeType) = try await MediaOptimizer.shared.optimizeImage(image, mode: .optimized)
        defer { try? FileManager.default.removeItem(at: url) }

        XCTAssertEqual(mimeType, "image/heic")
        XCTAssertTrue(url.pathExtension == "heic")
        XCTAssertTrue(FileManager.default.fileExists(atPath: url.path))
    }

    func testOptimizeUIImageOriginalMode() async throws {
        let renderer = UIGraphicsImageRenderer(size: CGSize(width: 200, height: 200))
        let image = renderer.image { ctx in
            UIColor.red.setFill()
            ctx.fill(CGRect(x: 0, y: 0, width: 200, height: 200))
        }

        let (url, mimeType) = try await MediaOptimizer.shared.optimizeImage(image, mode: .original)
        defer { try? FileManager.default.removeItem(at: url) }

        XCTAssertEqual(mimeType, "image/jpeg")
        XCTAssertTrue(url.pathExtension == "jpg")
        XCTAssertTrue(FileManager.default.fileExists(atPath: url.path))
    }

    // MARK: - Filename Update Helpers

    func testUpdatedFilenameJPEGToHEIC() {
        let result = MediaOptimizer.updatedFilename("IMG_12345.jpg", newMimeType: "image/heic")
        XCTAssertEqual(result, "IMG_12345.heic")
    }

    func testUpdatedFilenamePNGToHEIC() {
        let result = MediaOptimizer.updatedFilename("photo.png", newMimeType: "image/heic")
        XCTAssertEqual(result, "photo.heic")
    }

    func testUpdatedFilenameMOVToMP4() {
        let result = MediaOptimizer.updatedFilename("VID_20260217.mov", newMimeType: "video/mp4")
        XCTAssertEqual(result, "VID_20260217.mp4")
    }

    func testUpdatedFilenameUnchangedForUnknownMime() {
        let result = MediaOptimizer.updatedFilename("document.pdf", newMimeType: "application/pdf")
        XCTAssertEqual(result, "document.pdf")
    }

    func testUpdatedFilenamePreservesBaseName() {
        let result = MediaOptimizer.updatedFilename("My Vacation Photo.jpeg", newMimeType: "image/heic")
        XCTAssertEqual(result, "My Vacation Photo.heic")
    }

    func testUpdatedFilenameHEIFMime() {
        let result = MediaOptimizer.updatedFilename("photo.tiff", newMimeType: "image/heif")
        XCTAssertEqual(result, "photo.heic")
    }

    // MARK: - Mode Enum

    func testModeRawValues() {
        XCTAssertEqual(MediaOptimizer.Mode(rawValue: "optimized"), .optimized)
        XCTAssertEqual(MediaOptimizer.Mode(rawValue: "original"), .original)
        XCTAssertNil(MediaOptimizer.Mode(rawValue: "invalid"))
    }

    func testModeDefaultsToOptimized() {
        let mode = MediaOptimizer.Mode(rawValue: "optimized") ?? .original
        XCTAssertEqual(mode, .optimized)
    }

    // MARK: - Video Optimization

    func testVideoOptimization() async throws {
        let videoURL = try await createMinimalVideo()
        defer { try? FileManager.default.removeItem(at: videoURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: videoURL, mimeType: "video/quicktime", mode: .optimized
        )
        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        // Minimal videos may or may not get optimized depending on the codec/preset availability
        // but the method should not crash and should return valid results
        if wasOptimized {
            XCTAssertEqual(mimeType, "video/mp4")
            XCTAssertTrue(outputURL.pathExtension == "mp4")
            XCTAssertTrue(FileManager.default.fileExists(atPath: outputURL.path))
        } else {
            // If HEVC preset isn't available or video is already optimal, passthrough is fine
            XCTAssertEqual(mimeType, "video/quicktime")
        }
    }

    func testPortraitVideoPreservesNaturalSize() async throws {
        // Create a portrait video (1080x1920 natural with 90° rotation transform)
        let videoURL = try await createMinimalVideo(width: 1920, height: 1080, rotated: true)
        defer { try? FileManager.default.removeItem(at: videoURL) }

        let (outputURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
            fileURL: videoURL, mimeType: "video/quicktime", mode: .optimized
        )
        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        if wasOptimized {
            XCTAssertEqual(mimeType, "video/mp4")

            // Verify the output video track dimensions match naturalSize (not transformed)
            let asset = AVURLAsset(url: outputURL)
            guard let track = try? await asset.loadTracks(withMediaType: .video).first else {
                XCTFail("No video track in output")
                return
            }
            let outputSize = try await track.load(.naturalSize)
            // Writer dimensions should be based on naturalSize (1920x1080),
            // NOT the transformed size (1080x1920)
            XCTAssertEqual(Int(outputSize.width), 1920)
            XCTAssertEqual(Int(outputSize.height), 1080)
        }
        // If not optimized (e.g., bitrate already low), that's acceptable
    }

    // MARK: - Video Helper

    /// Creates a minimal 1-second video file for testing.
    /// - Parameters:
    ///   - width: Pixel buffer width (default 320)
    ///   - height: Pixel buffer height (default 240)
    ///   - rotated: If true, applies a 90° clockwise rotation transform (simulates portrait recording)
    private func createMinimalVideo(width: Int = 320, height: Int = 240, rotated: Bool = false) async throws -> URL {
        let outputURL = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("mov")

        // Create a simple video using AVAssetWriter
        let writer = try AVAssetWriter(outputURL: outputURL, fileType: .mov)

        let videoSettings: [String: Any] = [
            AVVideoCodecKey: AVVideoCodecType.h264,
            AVVideoWidthKey: width,
            AVVideoHeightKey: height
        ]

        let writerInput = AVAssetWriterInput(mediaType: .video, outputSettings: videoSettings)
        if rotated {
            // 90° clockwise rotation (portrait video recorded in landscape sensor)
            writerInput.transform = CGAffineTransform(rotationAngle: .pi / 2)
        }
        let adaptor = AVAssetWriterInputPixelBufferAdaptor(
            assetWriterInput: writerInput,
            sourcePixelBufferAttributes: [
                kCVPixelBufferPixelFormatTypeKey as String: REDACTED,
                kCVPixelBufferWidthKey as String: width,
                kCVPixelBufferHeightKey as String: height
            ]
        )
        writerInput.expectsMediaDataInRealTime = false
        writer.add(writerInput)
        writer.startWriting()
        writer.startSession(atSourceTime: .zero)

        // Write 30 frames at 30fps = 1 second
        for i in 0..<30 {
            while !writerInput.isReadyForMoreMediaData {
                Thread.sleep(forTimeInterval: 0.01)
            }

            var pixelBuffer: CVPixelBuffer?
            CVPixelBufferCreate(
                kCFAllocatorDefault, width, height,
                REDACTED, nil, &pixelBuffer
            )
            guard let buffer = pixelBuffer else { continue }

            CVPixelBufferLockBaseAddress(buffer, [])
            if let baseAddress = CVPixelBufferGetBaseAddress(buffer) {
                // Fill with a color gradient per frame
                let bytesPerRow = CVPixelBufferGetBytesPerRow(buffer)
                let ptr = baseAddress.assumingMemoryBound(to: UInt8.self)
                for y in 0..<height {
                    for x in 0..<width {
                        let offset = y * bytesPerRow + x * 4
                        ptr[offset] = 255 // A
                        ptr[offset + 1] = UInt8(i * 8 % 256) // R
                        ptr[offset + 2] = UInt8(x * 255 / width) // G
                        ptr[offset + 3] = UInt8(y * 255 / height) // B
                    }
                }
            }
            CVPixelBufferUnlockBaseAddress(buffer, [])

            let time = CMTime(value: CMTimeValue(i), timescale: 30)
            adaptor.append(buffer, withPresentationTime: time)
        }

        writerInput.markAsFinished()
        await writer.finishWriting()

        guard writer.status == .completed else {
            throw writer.error ?? NSError(domain: "Test", code: 2, userInfo: [NSLocalizedDescriptionKey: "Video write failed"])
        }

        return outputURL
    }
}

