import AVFoundation
import CoreMedia
import ImageIO
import UniformTypeIdentifiers
import UIKit
import os.log

private let optimizerLogger = Logger(subsystem: "app.vaultaire.ios", category: "MediaOptimizer")

/// Optimizes media files before vault storage using modern codecs.
/// Images → HEIC (ImageIO), Videos → HEVC 1080p (AVAssetReader/AVAssetWriter with bitrate control).
/// Non-media files pass through unchanged.
///
/// Not an actor — all methods operate on temp files with no shared mutable state.
/// Actor isolation was serializing parallel import workers through a single executor,
/// defeating the 4-worker concurrency design in ParallelImporter.
final class MediaOptimizer: Sendable {
    static let shared = MediaOptimizer()

    enum Mode: String, Sendable {
        case optimized
        case original
    }

    /// Returns (outputURL, outputMimeType, wasOptimized).
    /// If `wasOptimized` is true, caller must delete the temp file after use.
    func optimize(fileURL: URL, mimeType: String, mode: Mode) async throws -> (URL, String, Bool) {
        guard mode == .optimized else {
            return (fileURL, mimeType, false)
        }

        if mimeType.hasPrefix("image/") {
            return try optimizeImage(fileURL: fileURL, mimeType: mimeType)
        } else if mimeType.hasPrefix("video/") {
            return try await optimizeVideo(fileURL: fileURL, mimeType: mimeType)
        }

        // Non-media: passthrough
        return (fileURL, mimeType, false)
    }

    /// Optimizes a UIImage (e.g., from camera) to HEIC temp file.
    /// Returns (tempFileURL, mimeType). Caller must delete temp file.
    func optimizeImage(_ image: UIImage, mode: Mode) async throws -> (URL, String) {
        if mode == .original {
            // Write as JPEG to temp file (avoids holding jpegData in memory alongside UIImage)
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
                .appendingPathExtension("jpg")
            guard let data = image.jpegData(compressionQuality: 0.8) else {
                throw OptimizationError.imageConversionFailed
            }
            try data.write(to: tempURL, options: [.atomic])
            return (tempURL, "image/jpeg")
        }

        // Write HEIC via ImageIO — avoids large Data intermediary
        guard let cgImage = image.cgImage else {
            throw OptimizationError.imageConversionFailed
        }

        let tempURL = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("heic")

        guard let destination = CGImageDestinationCreateWithURL(
            tempURL as CFURL,
            AVFileType.heic as CFString,
            1,
            nil
        ) else {
            throw OptimizationError.heicEncodingFailed
        }

        // Check if image has alpha to optimize file size and memory
        let hasAlpha = cgImage.alphaInfo != .none && cgImage.alphaInfo != .noneSkipFirst && cgImage.alphaInfo != .noneSkipLast
        let options: [CFString: Any] = [
            kCGImageDestinationLossyCompressionQuality: 0.6,
            kCGImagePropertyHasAlpha: hasAlpha
        ]
        CGImageDestinationAddImage(destination, cgImage, options as CFDictionary)

        guard CGImageDestinationFinalize(destination) else {
            FileUtilities.cleanupTemporaryFile(at: tempURL)
            throw OptimizationError.heicEncodingFailed
        }

        let fileSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0
        optimizerLogger.info("Camera image → HEIC: \(fileSize) bytes")

        return (tempURL, "image/heic")
    }

    // MARK: - Image Optimization

    private func optimizeImage(fileURL: URL, mimeType: String) throws -> (URL, String, Bool) {
        // Skip if already HEIC
        if mimeType == "image/heic" || mimeType == "image/heif" {
            return (fileURL, mimeType, false)
        }

        // autoreleasepool ensures CGImageSource, CGImage, and CGImageDestination temporaries
        // are released immediately. With 4 parallel workers, this prevents ~100-200MB of
        // accumulated ObjC objects between executor yields.
        return try autoreleasepool {
            guard let source = CGImageSourceCreateWithURL(fileURL as CFURL, nil) else {
                return (fileURL, mimeType, false)
            }

            // Check image dimensions for potential downsampling
            let properties = CGImageSourceCopyPropertiesAtIndex(source, 0, nil) as? [CFString: Any]
            let width = properties?[kCGImagePropertyPixelWidth] as? Int ?? 0
            let height = properties?[kCGImagePropertyPixelHeight] as? Int ?? 0
            let maxDimension = max(width, height)

            // Get source image — downsample if > 4096px
            // Read EXIF orientation to preserve it when writing output
            let orientation: CGImagePropertyOrientation
            let cgImage: CGImage
            if maxDimension > 4096 {
                let thumbOptions: [CFString: Any] = [
                    kCGImageSourceCreateThumbnailFromImageAlways: true,
                    kCGImageSourceCreateThumbnailWithTransform: true,
                    kCGImageSourceThumbnailMaxPixelSize: 4096
                ]
                guard let thumb = CGImageSourceCreateThumbnailAtIndex(source, 0, thumbOptions as CFDictionary) else {
                    return (fileURL, mimeType, false)
                }
                cgImage = thumb
                // kCGImageSourceCreateThumbnailWithTransform already rotated the pixels,
                // so mark as .up to avoid double-rotation in the output HEIC.
                orientation = .up
            } else {
                guard let full = CGImageSourceCreateImageAtIndex(source, 0, nil) else {
                    return (fileURL, mimeType, false)
                }
                cgImage = full
                // Full-size path: pixels are unrotated, so preserve original EXIF orientation.
                orientation = (properties?[kCGImagePropertyOrientation] as? UInt32).flatMap { CGImagePropertyOrientation(rawValue: $0) } ?? .up
            }

            // Write HEIC to temp file
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
                .appendingPathExtension("heic")

            guard let destination = CGImageDestinationCreateWithURL(
                tempURL as CFURL,
                AVFileType.heic as CFString,
                1,
                nil
            ) else {
                return (fileURL, mimeType, false)
            }

            // Preserve EXIF orientation metadata when writing the output image
            // Check if image has alpha to optimize file size and memory
            let hasAlpha = cgImage.alphaInfo != .none && cgImage.alphaInfo != .noneSkipFirst && cgImage.alphaInfo != .noneSkipLast
            let options: [CFString: Any] = [
                kCGImageDestinationLossyCompressionQuality: 0.6,
                kCGImagePropertyOrientation: orientation.rawValue,
                kCGImagePropertyHasAlpha: hasAlpha
            ]
            CGImageDestinationAddImage(destination, cgImage, options as CFDictionary)

            guard CGImageDestinationFinalize(destination) else {
                FileUtilities.cleanupTemporaryFile(at: tempURL)
                return (fileURL, mimeType, false)
            }

            let originalSize = (try? FileManager.default.attributesOfItem(atPath: fileURL.path)[.size] as? Int64) ?? 0
            let optimizedSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0
            optimizerLogger.info("Image optimized: \(originalSize) → \(optimizedSize) bytes (\(originalSize > 0 ? Int(100 - (optimizedSize * 100 / originalSize)) : 0)% reduction)")

            return (tempURL, "image/heic", true)
        }
    }

    // MARK: - Video Optimization

    /// Target bitrate tiers for HEVC encoding (bits per second).
    /// Higher targets = less compression work = faster hardware encoding (~30-50% speed gain)
    /// while still achieving significant reduction vs 4K source footage.
    private static let bitrate1080p = 8_000_000  // 8 Mbps — fast encode, ~50-70% smaller than 4K source
    private static let bitrate720p  = 4_000_000  // 4 Mbps
    private static let bitrateSD    = 2_000_000  // 2 Mbps
    private static let audioBitrate = 128_000    // 128 kbps AAC

    private func optimizeVideo(fileURL: URL, mimeType: String) async throws -> (URL, String, Bool) {
        let asset = AVURLAsset(url: fileURL)

        // Check if already compact enough — skip if bitrate is already low
        if await isAlreadyOptimalVideo(asset) {
            optimizerLogger.info("Video already optimal, skipping")
            return (fileURL, mimeType, false)
        }

        guard let videoTrack = try? await asset.loadTracks(withMediaType: .video).first else {
            return (fileURL, mimeType, false)
        }

        let naturalSize = try await videoTrack.load(.naturalSize)
        let preferredTransform = try await videoTrack.load(.preferredTransform)

        // Calculate output dimensions (max 1080p, preserve aspect ratio)
        let targetSize = Self.targetVideoSize(from: naturalSize, transform: preferredTransform)
        let targetBitrate = Self.targetBitrate(for: targetSize)

        optimizerLogger.info("Video transcode: \(Int(naturalSize.width))x\(Int(naturalSize.height)) → \(targetSize.width)x\(targetSize.height) @ \(targetBitrate / 1000) kbps")

        let tempURL = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString)
            .appendingPathExtension("mp4")

        do {
            // Set up reader
            let reader = try AVAssetReader(asset: asset)

            let videoReaderOutput = AVAssetReaderTrackOutput(track: videoTrack, outputSettings: [
                kCVPixelBufferPixelFormatTypeKey as String: REDACTED
            ])
            videoReaderOutput.alwaysCopiesSampleData = false
            reader.add(videoReaderOutput)

            // Set up writer
            let writer = try AVAssetWriter(outputURL: tempURL, fileType: .mp4)

            let videoWriterInput = AVAssetWriterInput(mediaType: .video, outputSettings: [
                AVVideoCodecKey: AVVideoCodecType.hevc,
                AVVideoWidthKey: targetSize.width,
                AVVideoHeightKey: targetSize.height,
                AVVideoCompressionPropertiesKey: [
                    AVVideoAverageBitRateKey: targetBitrate,
                    AVVideoExpectedSourceFrameRateKey: 30,
                    AVVideoMaxKeyFrameIntervalKey: 60
                ]
            ])
            videoWriterInput.expectsMediaDataInRealTime = false
            videoWriterInput.transform = preferredTransform
            writer.add(videoWriterInput)

            // Audio track (if present)
            var audioReaderOutput: AVAssetReaderTrackOutput?
            var audioWriterInput: AVAssetWriterInput?
            if let audioTrack = try? await asset.loadTracks(withMediaType: .audio).first {
                let audioOutput = AVAssetReaderTrackOutput(track: audioTrack, outputSettings: [
                    AVFormatIDKey: kAudioFormatLinearPCM,
                    AVLinearPCMBitDepthKey: 16,
                    AVLinearPCMIsBigEndianKey: false,
                    AVLinearPCMIsFloatKey: false,
                    AVLinearPCMIsNonInterleaved: false
                ])
                audioOutput.alwaysCopiesSampleData = false
                reader.add(audioOutput)
                audioReaderOutput = audioOutput

                let audioInput = AVAssetWriterInput(mediaType: .audio, outputSettings: [
                    AVFormatIDKey: kAudioFormatMPEG4AAC,
                    AVEncoderBitRateKey: Self.audioBitrate,
                    AVSampleRateKey: 44100,
                    AVNumberOfChannelsKey: 2
                ])
                audioInput.expectsMediaDataInRealTime = false
                writer.add(audioInput)
                audioWriterInput = audioInput
            }

            // Start reading/writing
            reader.startReading()
            writer.startWriting()
            writer.startSession(atSourceTime: .zero)

            // Process video and audio tracks concurrently.
            // Per-transcode queues so parallel video workers don't serialize on shared queues.
            let id = UUID().uuidString.prefix(8)
            let videoXferQueue = DispatchQueue(label: "app.vaultaire.transcode.video.\(id)")
            let audioXferQueue = DispatchQueue(label: "app.vaultaire.transcode.audio.\(id)")
            await withTaskGroup(of: Void.self) { group in
                group.addTask {
                    await Self.transferSamples(from: videoReaderOutput, to: videoWriterInput, on: videoXferQueue)
                }
                if let audioOutput = audioReaderOutput, let audioInput = audioWriterInput {
                    group.addTask {
                        await Self.transferSamples(from: audioOutput, to: audioInput, on: audioXferQueue)
                    }
                }
            }

            await writer.finishWriting()

            guard writer.status == .completed else {
                try? FileManager.default.removeItem(at: tempURL)
                let errorDesc = writer.error?.localizedDescription ?? "unknown"
                optimizerLogger.error("Video writer failed: \(errorDesc, privacy: .public)")
                return (fileURL, mimeType, false)
            }

            reader.cancelReading()
        } catch {
            try? FileManager.default.removeItem(at: tempURL)
            optimizerLogger.error("Video transcode setup failed: \(error.localizedDescription, privacy: .public)")
            return (fileURL, mimeType, false)
        }

        let originalSize = (try? FileManager.default.attributesOfItem(atPath: fileURL.path)[.size] as? Int64) ?? 0
        let optimizedSize = (try? FileManager.default.attributesOfItem(atPath: tempURL.path)[.size] as? Int64) ?? 0

        // If optimized file isn't meaningfully smaller (< 20% reduction), skip it — not worth the transcode time
        if originalSize > 0 && optimizedSize >= Int64(Double(originalSize) * 0.8) {
            try? FileManager.default.removeItem(at: tempURL)
            optimizerLogger.info("Video transcode yielded < 20% reduction (\(originalSize) → \(optimizedSize)), keeping original")
            return (fileURL, mimeType, false)
        }

        let reduction = originalSize > 0 ? Int(100 - (optimizedSize * 100 / originalSize)) : 0
        optimizerLogger.info("Video optimized: \(originalSize) → \(optimizedSize) bytes (\(reduction)% reduction)")

        return (tempURL, "video/mp4", true)
    }

    /// Transfer sample buffers from reader output to writer input using the proper callback pattern.
    /// Each transcode creates its own serial queue so parallel video workers don't serialize.
    private static func transferSamples(from output: AVAssetReaderTrackOutput, to input: AVAssetWriterInput, on queue: DispatchQueue) async {
        await withCheckedContinuation { (continuation: CheckedContinuation<Void, Never>) in
            // AVAssetWriterInput/AVAssetReaderTrackOutput are thread-safe for this callback pattern.
            // The requestMediaDataWhenReady callback is always called on the provided queue serially.
            nonisolated(unsafe) let output = output
            nonisolated(unsafe) let input = input
            var resumed = false
            input.requestMediaDataWhenReady(on: queue) {
                while input.isReadyForMoreMediaData {
                    guard let buffer = output.copyNextSampleBuffer() else {
                        input.markAsFinished()
                        if !resumed {
                            resumed = true
                            continuation.resume()
                        }
                        return
                    }
                    input.append(buffer)
                }
            }
        }
    }

    /// Calculate output video dimensions: cap at 1080p, preserve aspect ratio.
    /// Uses naturalSize directly — the writer's transform property handles orientation.
    private static func targetVideoSize(from naturalSize: CGSize, transform _: CGAffineTransform) -> (width: Int, height: Int) {
        // Use naturalSize directly — pixel buffers are in natural orientation,
        // and videoWriterInput.transform handles display rotation.
        let w = naturalSize.width
        let h = naturalSize.height
        let maxDim = max(w, h)

        if maxDim <= 1920 {
            // Already ≤ 1080p — keep original dimensions (rounded to even)
            return (Int(w / 2) * 2, Int(h / 2) * 2)
        }

        let scale = 1920.0 / maxDim
        // Round to nearest even number (required by video encoder)
        let newW = Int((w * scale) / 2) * 2
        let newH = Int((h * scale) / 2) * 2
        return (max(newW, 2), max(newH, 2))
    }

    /// Select target bitrate based on output resolution.
    private static func targetBitrate(for size: (width: Int, height: Int)) -> Int {
        let maxDim = max(size.width, size.height)
        if maxDim >= 1080 { return bitrate1080p }
        if maxDim >= 720 { return bitrate720p }
        return bitrateSD
    }

    /// Check if video is already optimal and can skip transcoding.
    /// Skips if HEVC and ≤ 1080p — bitrate doesn't matter since re-encoding
    /// from e.g. 15 Mbps to 8 Mbps yields diminishing savings at massive time cost.
    /// Only transcode when codec isn't HEVC (need re-encode) or resolution > 1080p (need downscale).
    private func isAlreadyOptimalVideo(_ asset: AVURLAsset) async -> Bool {
        guard let videoTrack = try? await asset.loadTracks(withMediaType: .video).first else {
            return false
        }

        // Check codec
        guard let formatDescriptions = try? await videoTrack.load(.formatDescriptions),
              let desc = formatDescriptions.first else {
            return false
        }

        let mediaSubType = CMFormatDescriptionGetMediaSubType(desc)
        // 'hvc1' FourCC = HEVC
        let isHEVC = mediaSubType == 0x68766331

        guard isHEVC else { return false }

        // Check resolution
        guard let naturalSize = try? await videoTrack.load(.naturalSize) else {
            return false
        }
        let maxDimension = max(naturalSize.width, naturalSize.height)
        guard maxDimension <= 1920 else { return false }

        let estimatedRate = (try? await videoTrack.load(.estimatedDataRate)) ?? 0
        optimizerLogger.info("HEVC ≤1080p @ \(Int(estimatedRate / 1000)) kbps — skipping transcode")

        return true
    }

    // MARK: - Errors

    enum OptimizationError: LocalizedError {
        case imageConversionFailed
        case heicEncodingFailed

        var errorDescription: String? {
            if self == .imageConversionFailed { return "Could not process image for optimization" }
            return "HEIC encoding failed"
        }
    }
}

// MARK: - Filename Helpers

extension MediaOptimizer {
    /// Updates filename extension to match the optimized format.
    static func updatedFilename(_ filename: String, newMimeType: String) -> String {
        let ext: String
        switch newMimeType {
        case "image/heic", "image/heif": ext = "heic"
        case "video/mp4": ext = "mp4"
        default: return filename
        }

        let name = (filename as NSString).deletingPathExtension
        return "\(name).\(ext)"
    }
}

import AVFoundation
import CoreMedia
import Foundation
import UIKit
import UserNotifications
import os.log

/// Processes staged imports from the share extension into the main vault storage.
/// Called after vault unlock when pending imports exist for the matching fingerprint.
private let importIngestorLogger = Logger(subsystem: "app.vaultaire.ios", category: "ImportIngestor")

enum ImportIngestor {

    struct ImportProgress: Sendable {
        let completed: Int
        let total: Int
    }

    struct ImportResult {
        let imported: Int
        let failed: Int
        let batchesCleaned: Int
        /// Primary failure reason (e.g. "Storage expansion requires premium")
        let failureReason: String?
    }

    /// Processes all pending batches matching the given vault key.
    /// Decrypts each file, stores it via VaultStorage, and cleans up the batch.
    static func processPendingImports(
        for vaultKey: VaultKey,
        onProgress: (@Sendable (ImportProgress) async -> Void)? = nil
    ) async -> ImportResult {
        let fingerprint = KeyDerivation.keyFingerprint(from: vaultKey.rawBytes)
        let batches = StagedImportManager.pendingBatches(for: fingerprint)

        guard !batches.isEmpty else {
            return ImportResult(imported: 0, failed: 0, batchesCleaned: 0, failureReason: nil)
        }

        let totalImportable = batches.reduce(0) { total, batch in
            total + StagedImportManager.importableFileCount(in: batch)
        }
        if totalImportable > 0, let onProgress {
            await onProgress(ImportProgress(completed: 0, total: totalImportable))
        }

        var totalImported = 0
        var totalFailed = 0
        var batchesCleaned = 0
        var lastFailureReason: String?
        var totalProcessed = 0

        for batch in batches {
            let batchResult = await processBatch(
                batch,
                vaultKey: vaultKey,
                completedOffset: totalProcessed,
                totalImportable: totalImportable,
                onProgress: onProgress
            )
            totalImported += batchResult.imported
            totalFailed += batchResult.failed
            totalProcessed += batchResult.processed
            if let reason = batchResult.failureReason {
                lastFailureReason = reason
            }

            if batchResult.failed == 0 {
                // All files imported successfully — delete the batch
                StagedImportManager.deleteBatch(batch.batchId)
                batchesCleaned += 1
            } else {
                // Some files failed — increment retry or delete
                let deleted = StagedImportManager.incrementRetryOrDelete(batchId: batch.batchId)
                if deleted {
                    batchesCleaned += 1
                    EmbraceManager.shared.captureError(
                        NSError(
                            domain: "ImportIngestor",
                            code: 1,
                            userInfo: [NSLocalizedDescriptionKey: "Batch \(batch.batchId) deleted after 2 retries"]
                        )
                    )
                }
            }
        }

        // Cancel any pending import notifications
        cancelPendingImportNotifications()

        // Clean up orphaned batches while we're at it
        StagedImportManager.cleanupOrphans()

        if totalImportable > 0, totalProcessed < totalImportable, let onProgress {
            await onProgress(ImportProgress(completed: totalImportable, total: totalImportable))
        }

        return ImportResult(imported: totalImported, failed: totalFailed, batchesCleaned: batchesCleaned, failureReason: lastFailureReason)
    }

    private static func processBatch(
        _ batch: StagedImportManifest,
        vaultKey: VaultKey,
        completedOffset: Int,
        totalImportable: Int,
        onProgress: (@Sendable (ImportProgress) async -> Void)?
    ) async -> (imported: Int, failed: Int, processed: Int, failureReason: String?) {
        // Parallel processing with concurrency control
        let maxConcurrentImports = min(4, ProcessInfo.processInfo.processorCount)

        actor BatchState {
            var imported = 0
            var skipped = 0
            var failed = 0
            var failureReason: String?
            var processed = 0

            func recordImported() { imported += 1 }
            func recordSkipped() { skipped += 1 }
            func recordFailed(reason: String?) {
                failed += 1
                if let reason { failureReason = reason }
            }
            func incrementProcessed() { processed += 1 }
        }

        let state = BatchState()
        let progressInterval: TimeInterval = 0.5
        var lastProgressUpdate = Date()

        await VaultStorage.shared.beginImportBatch()
        await withTaskGroup(of: Void.self) { group in
            var inFlight = 0
            var fileIndex = 0
            let files = batch.files

            func addTasksUpToLimit() {
                while inFlight < maxConcurrentImports && fileIndex < files.count {
                    let file = files[fileIndex]
                    fileIndex += 1
                    inFlight += 1

                    group.addTask {
                        // Check if file was already imported
                        guard let encryptedFileURL = StagedImportManager.encryptedFileURL(
                            batchId: batch.batchId,
                            fileId: file.fileId
                        ) else {
                            await state.recordSkipped()
                            await state.incrementProcessed()
                            return
                        }

                        do {
                            // Decrypt to temp file
                            let tempURL = FileManager.default.temporaryDirectory
                                .appendingPathComponent("\(file.fileId.uuidString)_import")
                                .appendingPathExtension(URL(string: file.filename)?.pathExtension ?? "dat")
                            try CryptoEngine.decryptStagedFileToURL(
                                from: encryptedFileURL,
                                to: tempURL,
                                with: vaultKey.rawBytes
                            )

                            // Optimize media
                            let optimizationModeStr = UserDefaults.standard.string(forKey: "fileOptimization") ?? "optimized"
                            let optimizationMode = MediaOptimizer.Mode(rawValue: optimizationModeStr) ?? .optimized

                            let (optimizedURL, optimizedMimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
                                fileURL: tempURL, mimeType: file.mimeType, mode: optimizationMode
                            )

                            let storedFilename = wasOptimized
                                ? MediaOptimizer.updatedFilename(file.filename, newMimeType: optimizedMimeType)
                                : file.filename

                            // Process thumbnail and store
                            try await storeWithThumbnail(
                                batch: batch, file: file, optimizedURL: optimizedURL,
                                storedFilename: storedFilename, optimizedMimeType: optimizedMimeType,
                                vaultKey: vaultKey
                            )

                            if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
                            try? FileManager.default.removeItem(at: tempURL)

                            StagedImportManager.deleteEncryptedFile(batchId: batch.batchId, fileId: file.fileId)

                            await state.recordImported()
                            importIngestorLogger.info("Imported file: \(file.filename, privacy: .public)")
                        } catch {
                            await state.recordFailed(reason: error.localizedDescription)
                            importIngestorLogger.error("Failed to import \(file.fileId, privacy: .public): \(error.localizedDescription, privacy: .public)")
                        }

                        await state.incrementProcessed()
                    }
                }
            }

            addTasksUpToLimit()

            for await _ in group {
                inFlight -= 1
                addTasksUpToLimit()

                // Throttled progress updates
                let now = Date()
                let processed = await state.processed
                if now.timeIntervalSince(lastProgressUpdate) >= progressInterval || processed == files.count {
                    lastProgressUpdate = now
                    if totalImportable > 0, let onProgress {
                        await onProgress(
                            ImportProgress(
                                completed: min(completedOffset + processed, totalImportable),
                                total: totalImportable
                            )
                        )
                    }
                }
            }
        }

        do {
            try await VaultStorage.shared.endImportBatch(key: vaultKey)
        } catch {
            importIngestorLogger.error("Failed to flush import batch: \(error.localizedDescription, privacy: .public)")
        }

        let skipped = await state.skipped
        if skipped > 0 {
            importIngestorLogger.info("Batch \(batch.batchId, privacy: .public): skipped \(skipped) already-imported files")
        }

        return (await state.imported, await state.failed, await state.processed, await state.failureReason)
    }

    /// Resolves thumbnail data and stores the file inside an autoreleasepool.
    private static func storeWithThumbnail(
        batch: StagedImportManifest,
        file: StagedFileMetadata,
        optimizedURL: URL,
        storedFilename: String,
        optimizedMimeType: String,
        vaultKey: VaultKey
    ) async throws {
        // Resolve thumbnail inside autoreleasepool so UIImage/CGImage temporaries are released
        let thumbnailData: Data? = autoreleasepool {
            var thumb: Data?
            if file.hasThumbnail,
               let encThumb = StagedImportManager.readEncryptedThumbnail(
                   batchId: batch.batchId, fileId: file.fileId
               ) {
                thumb = try? CryptoEngine.decrypt(encThumb, with: vaultKey.rawBytes)
            }
            if thumb == nil {
                thumb = generateThumbnailSync(for: optimizedURL, mimeType: optimizedMimeType)
            }
            return thumb
        }
        // storeFileFromURL is now async — must be called outside autoreleasepool
        _ = try await VaultStorage.shared.storeFileFromURL(
            optimizedURL,
            filename: storedFilename,
            mimeType: optimizedMimeType,
            with: vaultKey,
            thumbnailData: thumbnailData
        )
    }

    // MARK: - Thumbnail Generation

    /// Generates a thumbnail synchronously from the decrypted temp file.
    /// Runs inside autoreleasepool so UIImage/CGImage temporaries are released.
    private static func generateThumbnailSync(for fileURL: URL, mimeType: String) -> Data? {
        if mimeType.hasPrefix("image/") {
            return FileUtilities.generateThumbnail(fromFileURL: fileURL)
        } else if mimeType.hasPrefix("video/") {
            let asset = AVAsset(url: fileURL)
            let generator = AVAssetImageGenerator(asset: asset)
            generator.appliesPreferredTrackTransform = true
            generator.maximumSize = CGSize(width: 400, height: 400)
            let time = CMTime(seconds: 0.5, preferredTimescale: 600)
            guard let cgImage = try? generator.copyCGImage(at: time, actualTime: nil) else { return nil }
            return UIImage(cgImage: cgImage).jpegData(compressionQuality: 0.7)
        }
        return nil
    }

    private static func cancelPendingImportNotifications() {
        let center = UNUserNotificationCenter.current()
        center.getPendingNotificationRequests { requests in
            let importIds = requests
                .filter { $0.identifier.hasPrefix("pending-import-") }
                .map { $0.identifier }
            center.removePendingNotificationRequests(withIdentifiers: importIds)
        }
    }
}

