  6→private let parallelImporterLogger = Logger(subsystem: "app.vaultaire.ios", category: "ParallelImporter")
  7→
  8→/// Runs import work off MainActor with dedicated worker tasks.
  9→/// 2 video-priority workers + 2 image-priority workers = 4 parallel workers.
 10→/// Workers drain their primary queue first, then steal from the other queue.
 11→/// Results stream back to MainActor via AsyncStream for real-time UI updates.
 12→enum ParallelImporter {
 13→
 14→    // MARK: - Work Items
 15→
 16→    /// Photo picker work item — wraps NSItemProvider (thread-safe by design)
 17→    struct PickerWorkItem: @unchecked Sendable {
 18→        let originalIndex: Int
 19→        let provider: NSItemProvider
 20→    }
 21→
 22→    /// File/document picker work item
 23→    struct URLWorkItem: @unchecked Sendable {
 24→        let originalIndex: Int
 25→        let url: URL
 26→    }
 27→
 28→    // MARK: - Config
 29→
 30→    /// Groups common import parameters to reduce function parameter count.
 31→    struct ImportConfig: Sendable {
 32→        let key: VaultKey
 33→        let encryptionKey: Data
 34→        let optimizationMode: MediaOptimizer.Mode
 35→    }
 36→
 37→    // MARK: - Events
 38→
 39→    enum ImportEvent: Sendable {
 40→        case imported(VaultFileItem)
 41→        case failed(reason: String?)
 42→    }
 43→
 44→    // MARK: - Thread-Safe Queue
 45→
 46→    actor Queue<T: Sendable> {
 47→        private var items: [T]
 48→        private var idx = 0
 49→
 50→        init(_ items: [T]) { self.items = items }
 51→
 52→        func next() -> T? {
 53→            guard idx < items.count else { return nil }
 54→            let item = items[idx]
 55→            idx += 1
 56→            return item
 57→        }
 58→
 59→        func remainingCount() -> Int {
 60→            return items.count - idx
 61→        }
 62→    }
 63→
 64→    // MARK: - Generic Worker Runner
 65→
 66→    /// Runs concurrent workers consuming from queues, streaming results via continuation.
 67→    /// Each (queue, workerCount) pair spawns `workerCount` tasks pulling from that queue.
 68→    static func runWorkers<Work: Sendable>(
 69→        queues: [(Queue<Work>, Int)],
 70→        process: @escaping @Sendable (Work) async throws -> VaultFileItem?,
 71→        continuation: AsyncStream<ImportEvent>.Continuation
 72→    ) async {
 73→        await withTaskGroup(of: Void.self) { group in
 74→            for (queue, count) in queues {
 75→                for _ in 0..<count {
 76→                    group.addTask {
 77→                        while let item = await queue.next() {
 78→                            guard !Task.isCancelled else { return }
 79→                            do {
 80→                                if let file = try await process(item) {
 81→                                    continuation.yield(.imported(file))
 82→                                }
 83→                            } catch {
 84→                                continuation.yield(.failed(reason: error.localizedDescription))
 85→                            }
 86→                        }
 87→                    }
 88→                }
 89→            }
 90→        }
 91→    }
 92→
 93→    // MARK: - Photo Picker Import
 94→
 95→    static func runPhotoImport(
 96→        videoWork: [PickerWorkItem],
 97→        imageWork: [PickerWorkItem],
 98→        videoWorkerCount: Int,
 99→        imageWorkerCount: Int,
100→        config: ImportConfig,
101→        continuation: AsyncStream<ImportEvent>.Continuation
102→    ) async {
103→        let videoQueue = Queue(videoWork)
104→        let imageQueue = Queue(imageWork)
105→
106→        await withTaskGroup(of: Void.self) { group in
107→            // Video-priority workers: drain videos first, then steal from image queue
108→            for _ in 0..<videoWorkerCount {
109→                group.addTask {
110→                    while let item = await videoQueue.next() {
111→                        guard !Task.isCancelled else { return }
112→                        do {
113→                            if let file = try await importVideo(item: item, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
114→                                continuation.yield(.imported(file))
115→                            }
116→                        } catch {
117→                            continuation.yield(.failed(reason: error.localizedDescription))
118→                        }
119→                    }
120→                    // Steal remaining images
121→                    while let item = await imageQueue.next() {
122→                        guard !Task.isCancelled else { return }
123→                        do {
124→                            if let file = try await importImage(item: item, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
125→                                continuation.yield(.imported(file))
126→                            }
127→                        } catch {
128→                            continuation.yield(.failed(reason: error.localizedDescription))
129→                        }
130→                    }
131→                }
132→            }
133→
134→            // Image-priority workers: drain images first, then steal from video queue
135→            for _ in 0..<imageWorkerCount {
136→                group.addTask {
137→                    while let item = await imageQueue.next() {
138→                        guard !Task.isCancelled else { return }
139→                        do {
140→                            if let file = try await importImage(item: item, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
141→                                continuation.yield(.imported(file))
142→                            }
143→                        } catch {
144→                            continuation.yield(.failed(reason: error.localizedDescription))
145→                        }
146→                    }
147→                    // Steal remaining videos
148→                    while let item = await videoQueue.next() {
149→                        guard !Task.isCancelled else { return }
150→                        do {
151→                            if let file = try await importVideo(item: item, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
152→                                continuation.yield(.imported(file))
153→                            }
154→                        } catch {
155→                            continuation.yield(.failed(reason: error.localizedDescription))
156→                        }
157→                    }
158→                }
159→            }
160→        }
161→    }
162→
163→    // MARK: - File Import
164→
165→    static func runFileImport(
166→        videoWork: [URLWorkItem],
167→        otherWork: [URLWorkItem],
168→        videoWorkerCount: Int,
169→        otherWorkerCount: Int,
170→        config: ImportConfig,
171→        continuation: AsyncStream<ImportEvent>.Continuation
172→    ) async {
173→        let videoQueue = Queue(videoWork)
174→        let otherQueue = Queue(otherWork)
175→
176→        await withTaskGroup(of: Void.self) { group in
177→            // Video-priority workers: drain videos first, then steal from other queue
178→            for _ in 0..<videoWorkerCount {
179→                group.addTask {
180→                    while let item = await videoQueue.next() {
181→                        guard !Task.isCancelled else { return }
182→                        do {
183→                            if let file = try await importFileFromURL(url: item.url, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
184→                                continuation.yield(.imported(file))
185→                            }
186→                        } catch {
187→                            continuation.yield(.failed(reason: error.localizedDescription))
188→                        }
189→                    }
190→                    // Steal remaining non-video files
191→                    while let item = await otherQueue.next() {
192→                        guard !Task.isCancelled else { return }
193→                        do {
194→                            if let file = try await importFileFromURL(url: item.url, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
195→                                continuation.yield(.imported(file))
196→                            }
197→                        } catch {
198→                            continuation.yield(.failed(reason: error.localizedDescription))
199→                        }
200→                    }
201→                }
202→            }
203→
204→            // Other-priority workers: drain other files first, then steal from video queue
205→            for _ in 0..<otherWorkerCount {
206→                group.addTask {
207→                    while let item = await otherQueue.next() {
208→                        guard !Task.isCancelled else { return }
209→                        do {
210→                            if let file = try await importFileFromURL(url: item.url, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
211→                                continuation.yield(.imported(file))
212→                            }
213→                        } catch {
214→                            continuation.yield(.failed(reason: error.localizedDescription))
215→                        }
216→                    }
217→                    // Steal remaining videos
218→                    while let item = await videoQueue.next() {
219→                        guard !Task.isCancelled else { return }
220→                        do {
221→                            if let file = try await importFileFromURL(url: item.url, key: config.key, encryptionKey: config.encryptionKey, optimizationMode: config.optimizationMode) {
222→                                continuation.yield(.imported(file))
223→                            }
224→                        } catch {
225→                            continuation.yield(.failed(reason: error.localizedDescription))
226→                        }
227→                    }
228→                }
229→            }
230→        }
231→    }
