233→    // MARK: - Individual Import Operations (run off MainActor)
234→
235→    private static func importVideo(
236→        item: PickerWorkItem,
237→        key: VaultKey,
238→        encryptionKey: Data,
239→        optimizationMode: MediaOptimizer.Mode
240→    ) async throws -> VaultFileItem? {
241→        guard !Task.isCancelled else { return nil }
242→
243→        let tempVideoURL = try await loadVideoURL(from: item.provider)
244→        defer { try? FileManager.default.removeItem(at: tempVideoURL) }
245→
246→        guard !Task.isCancelled else { return nil }
247→
248→        let ext = tempVideoURL.pathExtension.isEmpty ? "mov" : tempVideoURL.pathExtension
249→        let mime = FileUtilities.mimeType(forExtension: ext)
