350→    private static func importFileFromURL(
351→        url: URL,
352→        key: VaultKey,
353→        encryptionKey: Data,
354→        optimizationMode: MediaOptimizer.Mode
355→    ) async throws -> VaultFileItem? {
356→        guard !Task.isCancelled else { return nil }
357→
358→        guard url.startAccessingSecurityScopedResource() else {
359→            throw ImportError.accessDenied
360→        }
361→        defer { url.stopAccessingSecurityScopedResource() }
362→
363→        let originalFilename = url.lastPathComponent
364→        let sourceMimeType = FileUtilities.mimeType(forExtension: url.pathExtension)
365→
366→        let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
367→            fileURL: url, mimeType: sourceMimeType, mode: optimizationMode
368→        )
369→        defer {
370→            if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
371→        }
372→
373→        let filename = wasOptimized
374→            ? MediaOptimizer.updatedFilename(originalFilename, newMimeType: mimeType)
375→            : originalFilename
376→
377→        // Extract original date from the source file's filesystem creation date
378→        let resourceOriginalDate = (try? url.resourceValues(forKeys: [.creationDateKey]))?.creationDate
379→
380→        if mimeType.hasPrefix("video/") {
381→            let metadata = await generateVideoMetadata(from: optimizedURL)
382→            let originalDate = metadata.creationDate ?? resourceOriginalDate
383→            let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
384→
385→            let fileId = try await VaultStorage.shared.storeFileFromURL(
386→                optimizedURL, filename: filename, mimeType: mimeType,
387→                with: key, options: FileStoreOptions(thumbnailData: metadata.thumbnail, duration: metadata.duration, originalDate: originalDate)
388→            )
