395→                hasThumbnail: encThumb != nil, mimeType: mimeType,
396→                filename: filename, createdAt: Date(), duration: metadata.duration,
397→                originalDate: originalDate
398→            )
399→        } else {
400→            let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
401→            let thumbnail = mimeType.hasPrefix("image/")
402→                ? FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
403→                : nil
404→
405→            // For images, try EXIF first, then fall back to filesystem date
406→            let originalDate: Date? = mimeType.hasPrefix("image/")
