408→                : resourceOriginalDate
409→
410→            let fileId = try await VaultStorage.shared.storeFileFromURL(
411→                optimizedURL, filename: filename, mimeType: mimeType,
412→                with: key, thumbnailData: thumbnail, originalDate: originalDate
413→            )
414→
415→            let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
416→            if let encThumb { await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb) }
417→
418→            return VaultFileItem(
419→                id: fileId, size: fileSize,
420→                hasThumbnail: encThumb != nil, mimeType: mimeType,
421→                filename: filename, createdAt: Date(), originalDate: originalDate
422→            )
423→        }
424→    }
