426→    // MARK: - Utilities (nonisolated — no MainActor dependency)
427→
428→    /// Loads an image from PHPicker provider to a temp URL, preserving EXIF metadata.
429→    private static func loadImageURL(from provider: NSItemProvider) async throws -> URL {
430→        try await withCheckedThrowingContinuation { continuation in
431→            provider.loadFileRepresentation(forTypeIdentifier: UTType.image.identifier) { url, error in
432→                if let error {
433→                    continuation.resume(throwing: error)
434→                    return
435→                }
