            let mime = FileUtilities.mimeType(forExtension: ext)
            XCTAssertEqual(mime, expected, "Extension '\(ext)' should map to '\(expected)'")
            XCTAssertTrue(mime.hasPrefix("image/"), "'\(ext)' mime should start with image/")
