 96→struct VaultView: View {
 97→    @Environment(AppState.self) var appState
 98→    @Environment(SubscriptionManager.self) var subscriptionManager
 99→    @State var viewModel = VaultViewModel()
100→
101→    // UI-only presentation state
102→    @State var selectedFile: VaultFileItem?
103→    @State var selectedPhotoIndex: Int?
104→    @State var selectedVideoFile: VaultFileItem?
