261→                                viewModel.files.remove(at: idx)
262→                            }
263→                            // Trigger sync for shared vaults after deletion
264→                            if let key = appState.currentVaultKey {
265→                                ShareSyncManager.shared.scheduleSync(vaultKey: key)
266→                            }
267→                        },
268→                        allowDownloads: viewModel.sharePolicy?.allowDownloads ?? true
