276→                    file: file,
277→                    vaultKey: appState.currentVaultKey,
278→                    onDelete: viewModel.isSharedVault ? nil : { deletedId in
279→                        if let idx = viewModel.files.firstIndex(where: { $0.id == deletedId }) {
280→                            viewModel.files.remove(at: idx)
281→                        }
282→                        selectedFile = nil
283→                        // Trigger sync for shared vaults after deletion
284→                        if let key = appState.currentVaultKey {
285→                            ShareSyncManager.shared.scheduleSync(vaultKey: key)
286→                        }
287→                    },
288→                    allowDownloads: viewModel.sharePolicy?.allowDownloads ?? true
289→                )
290→            }
291→            .fullScreenCover(item: $selectedVideoFile) { file in
