311→                Button("Cancel", role: .cancel) { /* No-op */ }
312→                Button("Delete", role: .destructive) { viewModel.batchDelete() }
313→            } message: {
314→                Text("These files will be permanently deleted from the vault.")
315→            }
316→            .premiumPaywall(isPresented: $showingPaywall)
317→            .alert("Free Plan Limit", isPresented: $showingLimitAlert) {
318→                if limitAlertRemaining > 0 {
319→                    Button("Import \(limitAlertRemaining) Files") {
320→                        viewModel.proceedWithLimitedImport(limitAlertRemaining: limitAlertRemaining)
321→                    }
322→                }
323→                Button("Upgrade to PRO") {
324→                    viewModel.pendingImport = nil
325→                    showingPaywall = true
326→                }
327→                Button("Cancel", role: .cancel) {
328→                    viewModel.pendingImport = nil
