344→        .background { sheetsAndAlerts(visible: visible) }
345→        .task {
346→            viewModel.configure(appState: appState, subscriptionManager: subscriptionManager)
347→            viewModel.loadVault()
348→            ShareUploadManager.shared.resumePendingUploadsIfNeeded(trigger: "vault_view_task")
349→        }
350→        .onAppear {
351→            // Check shared vault status immediately when view appears
352→            // This ensures revoked shares are detected right away
353→            viewModel.checkSharedVaultStatus()
354→            // Report activity to inactivity lock manager
355→            InactivityLockManager.shared.userDidInteract()
356→        }
357→        .onTapGesture {
358→            // Report any tap as user activity
359→            InactivityLockManager.shared.userDidInteract()
360→        }
361→        .onChange(of: appState.currentVaultKey) { oldKey, newKey in
362→            viewModel.handleVaultKeyChange(oldKey: oldKey, newKey: newKey)
363→        }
364→        .onChange(of: showingSettings) { _, isShowing in
365→            if !isShowing {
366→                viewModel.loadFiles()
367→                viewModel.checkSharedVaultStatus()
368→            }
369→        }
370→        .onChange(of: showingPhotoPicker) { _, _ in
371→            appState.suppressLockForShareSheet = showingPhotoPicker || showingFilePicker
372→        }
373→        .onChange(of: showingFilePicker) { _, _ in
374→            appState.suppressLockForShareSheet = showingPhotoPicker || showingFilePicker
375→        }
376→        .onChange(of: viewModel.transferManager.status) { _, newStatus in
377→            if case .importComplete = newStatus {
378→                viewModel.loadVault()
379→                Task {
380→                    try? await Task.sleep(for: .seconds(2))
381→                    viewModel.transferManager.reset()
382→                }
383→            }
384→        }
385→        .onChange(of: viewModel.exportURLs) { _, urls in
386→            guard !urls.isEmpty else { return }
387→            ShareSheetHelper.present(items: urls) {
388→                viewModel.cleanupExportFiles()
389→                viewModel.selectedIds.removeAll()
390→                viewModel.isEditing = false
391→            }
392→        }
393→        .ignoresSafeArea(.keyboard)
394→        .preventScreenshots()
395→    }
396→}
