398→// MARK: - Vault File Item
399→
400→struct VaultFileItem: Identifiable, Sendable, Equatable {
401→    let id: UUID
402→    let size: Int
403→    let hasThumbnail: Bool
404→    let mimeType: String?
405→    let filename: String?
406→    let createdAt: Date?
407→    let duration: TimeInterval?
408→
409→    init(id: UUID, size: Int, hasThumbnail: Bool = false, mimeType: String?, filename: String?, createdAt: Date? = nil, duration: TimeInterval? = nil) {
410→        self.id = id
411→        self.size = size
412→        self.hasThumbnail = hasThumbnail
413→        self.mimeType = mimeType
414→        self.filename = filename
415→        self.createdAt = createdAt
416→        self.duration = duration
417→    }
418→
419→    var isImage: Bool {
420→        (mimeType ?? "").hasPrefix("image/")
421→    }
422→
423→    var isMedia: Bool {
424→        let mime = mimeType ?? ""
425→        return mime.hasPrefix("image/") || mime.hasPrefix("video/")
426→    }
427→}
428→
429→// MARK: - Photo Picker Wrapper
430→
431→struct PhotoPicker: UIViewControllerRepresentable {
432→    let onPhotosSelected: ([PHPickerResult]) -> Void
433→
434→    func makeUIViewController(context: Context) -> PHPickerViewController {
435→        var config = PHPickerConfiguration()
436→        config.selectionLimit = 0
437→        config.filter = .any(of: [.images, .videos])
438→
439→        let picker = PHPickerViewController(configuration: config)
440→        picker.delegate = context.coordinator
441→        return picker
442→    }
443→
444→    func updateUIViewController(_ _: PHPickerViewController, context _: Context) { /* No update needed */ }
445→
446→    func makeCoordinator() -> Coordinator {
447→        Coordinator(onPhotosSelected: onPhotosSelected)
448→    }
449→
450→    class Coordinator: NSObject, PHPickerViewControllerDelegate {
451→        let onPhotosSelected: ([PHPickerResult]) -> Void
452→
453→        init(onPhotosSelected: @escaping ([PHPickerResult]) -> Void) {
454→            self.onPhotosSelected = onPhotosSelected
455→        }
456→
457→        func picker(_ picker: PHPickerViewController, didFinishPicking results: [PHPickerResult]) {
458→            picker.dismiss(animated: true)
459→            guard !results.isEmpty else { return }
460→            onPhotosSelected(results)
461→        }
462→    }
463→}
