  7→
  8→private let vmLogger = Logger(subsystem: "app.vaultaire.ios", category: "VaultViewModel")
  9→
 10→@MainActor
 11→@Observable
 12→final class VaultViewModel {
 13→
 14→    // MARK: - Dependencies (set via configure())
 15→
 16→    private(set) weak var appState: AppState?
 17→    private(set) weak var subscriptionManager: SubscriptionManager?
 18→
 19→    // MARK: - Core Data State
 20→
 21→    var files: [VaultFileItem] = []
 22→    var masterKey: MasterKey?
 23→    var isLoading = true
 24→
 25→    // MARK: - Import State
 26→
 27→    var importProgress: (completed: Int, total: Int)?
 28→    var activeImportTask: Task<Void, Never>?
 29→    private var importGeneration = 0
 30→    var activeLoadTask: Task<Void, Never>?
 31→    var isImportingPendingFiles = false
 32→    var pendingImport: PendingImport?
 33→
 34→    var isImporting: Bool {
 35→        importProgress != nil || isImportingPendingFiles
 36→    }
 37→
 38→    // MARK: - Batch Edit State
 39→
 40→    var isEditing = false
 41→    var selectedIds: Set<UUID> = []
 42→    var isDeleteInProgress = false
 43→    var showingBatchDeleteConfirmation = false
 44→
 45→    // MARK: - Export State
 46→
 47→    var exportURLs: [URL] = []
 48→
 49→    // MARK: - Shared Vault State
 50→
 51→    var isSharedVault = false
 52→    var sharePolicy: VaultStorage.SharePolicy?
 53→    var sharedVaultId: String?
 54→    var updateAvailable = false
 55→    var isUpdating = false
 56→    var hasCountedOpenThisSession = false
 57→    var selfDestructMessage: String?
 58→    var showSelfDestructAlert = false
 59→
 60→    // MARK: - Filter / Sort State
 61→
 62→    var searchText = ""
 63→    var sortOrder: SortOrder = .dateNewest
 64→    var fileFilter: FileFilter = FileFilter(rawValue: UserDefaults.standard.string(forKey: "vaultFileFilter") ?? FileFilter.all.rawValue) ?? .all
 65→
 66→    // MARK: - Toast
 67→
 68→    var toastMessage: ToastMessage?
 69→
 70→    // MARK: - Transfer Manager
 71→
 72→    var transferManager = ShareImportManager.shared
 73→
 74→    // MARK: - Visible Files (computed from observed properties)
 75→
 76→    // MARK: - Computed
 77→
 78→    var useDateGrouping: Bool {
 79→        sortOrder == .dateNewest || sortOrder == .dateOldest
 80→    }
 81→
 82→    var fileOptimization: String = UserDefaults.standard.string(forKey: "fileOptimization") ?? "optimized"
 83→
 84→    // MARK: - Init
 85→
 86→    func configure(appState: AppState, subscriptionManager: SubscriptionManager) {
 87→        self.appState = appState
 88→        self.subscriptionManager = subscriptionManager
 89→    }
 90→
 91→    // MARK: - Visible Files
 92→
 93→    func computeVisibleFiles() -> VaultView.VisibleFiles {
 94→        var visible = files
 95→        switch fileFilter {
 96→        case .all:
 97→            break
 98→        case .media:
 99→            visible = visible.filter {
100→                let mime = $0.mimeType ?? ""
101→                return mime.hasPrefix("image/") || mime.hasPrefix("video/")
102→            }
103→        case .documents:
104→            visible = visible.filter {
105→                let mime = $0.mimeType ?? ""
106→                return !mime.hasPrefix("image/") && !mime.hasPrefix("video/")
107→            }
108→        }
109→        if !searchText.isEmpty {
110→            visible = visible.filter {
111→                ($0.filename ?? "").localizedStandardContains(searchText)
112→            }
113→        }
114→        switch sortOrder {
115→        case .dateNewest:
116→            visible.sort { ($0.createdAt ?? .distantPast) > ($1.createdAt ?? .distantPast) }
117→        case .dateOldest:
118→            visible.sort { ($0.createdAt ?? .distantPast) < ($1.createdAt ?? .distantPast) }
119→        case .sizeSmallest:
120→            visible.sort { $0.size < $1.size }
121→        case .sizeLargest:
122→            visible.sort { $0.size > $1.size }
123→        case .name:
124→            visible.sort { ($0.filename ?? "").localizedStandardCompare($1.filename ?? "") == .orderedAscending }
125→        }
126→
127→        let media = visible.filter { $0.isMedia }
128→        let documents = visible.filter { !$0.isMedia }
129→        let mediaIndexById = Dictionary(
130→            media.enumerated().map { ($1.id, $0) },
131→            uniquingKeysWith: { first, _ in first }
132→        )
133→        return VaultView.VisibleFiles(
134→            all: visible,
135→            media: media,
136→            documents: documents,
137→            mediaIndexById: mediaIndexById
138→        )
139→    }
140→
141→    /// Persists fileFilter to UserDefaults and updates the stored property.
142→    func setFileFilter(_ filter: FileFilter) {
143→        fileFilter = filter
144→        UserDefaults.standard.set(filter.rawValue, forKey: "vaultFileFilter")
145→    }
146→
147→    // MARK: - Load
148→
149→    func loadVault() {
150→        guard appState?.currentVaultKey != nil else {
151→            isLoading = false
152→            return
153→        }
154→        loadFiles()
155→        checkSharedVaultStatus()
156→    }
157→
158→    func loadFiles() {
159→        vmLogger.debug("loadFiles() called (current files.count=\(self.files.count), importActive=\(self.activeImportTask != nil))")
160→
161→        guard let key = appState?.currentVaultKey else {
162→            vmLogger.warning("loadFiles(): no vault key, aborting")
163→            isLoading = false
164→            return
165→        }
166→
167→        activeLoadTask?.cancel()
168→        activeLoadTask = Task.detached(priority: .userInitiated) {
169→            do {
170→                let result = try VaultStorage.shared.listFilesLightweight(with: key)
171→                for entry in result.files {
172→                    if let encThumb = entry.encryptedThumbnail {
173→                        await ThumbnailCache.shared.storeEncrypted(id: entry.fileId, data: encThumb)
174→                    }
175→                }
176→                let items = result.files.map { entry in
177→                    VaultFileItem(
178→                        id: entry.fileId,
179→                        size: entry.size,
180→                        hasThumbnail: entry.encryptedThumbnail != nil,
181→                        mimeType: entry.mimeType,
182→                        filename: entry.filename,
183→                        createdAt: entry.createdAt,
184→                        duration: entry.duration
185→                    )
186→                }
187→                await MainActor.run {
188→                    vmLogger.info("loadFiles() loaded \(items.count) files from disk (was \(self.files.count))")
189→                    self.masterKey = MasterKey(result.masterKey)
190→                    self.files = items
191→                    self.isLoading = false
192→
193→                    // Auto-detect best filter based on vault contents
194→                    if !items.isEmpty {
195→                        let hasNonMedia = items.contains { !$0.isMedia }
196→                        if !hasNonMedia && self.fileFilter == .all {
197→                            self.setFileFilter(.media)
198→                        } else if hasNonMedia && self.fileFilter == .media {
199→                            self.setFileFilter(.all)
200→                        }
201→                    }
202→
203→                    EmbraceManager.shared.addBreadcrumb(category: "vault.opened", data: ["fileCount": items.count])
204→                }
205→            } catch {
206→                vmLogger.error("loadFiles() failed: \(error.localizedDescription, privacy: .public)")
207→                await MainActor.run {
208→                    // Don't clear files on error - preserves in-memory state after imports
209→                    // If vault truly failed to load, user would see previous files or empty with error
210→                    self.isLoading = false
211→                }
212→            }
213→        }
214→    }
215→
216→    // MARK: - Selection
217→
218→    func toggleSelection(_ id: UUID) {
219→        if selectedIds.contains(id) {
220→            selectedIds.remove(id)
221→        } else {
222→            selectedIds.insert(id)
223→        }
224→    }
225→
226→    // MARK: - Batch Operations
227→
228→    func batchDelete() {
229→        guard let key = appState?.currentVaultKey else { return }
230→        let idsToDelete = selectedIds
231→        let count = idsToDelete.count
232→
233→        isDeleteInProgress = true
234→        importProgress = (0, count)
235→        IdleTimerManager.shared.disable()
236→        isEditing = false
237→
238→        activeImportTask?.cancel()
239→        activeImportTask = Task.detached(priority: .userInitiated) {
240→            do {
241→                try VaultStorage.shared.deleteFiles(ids: idsToDelete, with: key) { deleted in
242→                    Task { @MainActor in
243→                        guard !Task.isCancelled else { return }
244→                        self.importProgress = (deleted, count)
245→                    }
246→                }
247→            } catch {
248→                await MainActor.run {
249→                    self.toastMessage = .error("Delete failed: \(error.localizedDescription)")
250→                }
251→            }
252→
253→            await MainActor.run {
254→                guard !Task.isCancelled else { return }
255→                self.files.removeAll { idsToDelete.contains($0.id) }
256→                self.selectedIds.removeAll()
257→                self.importProgress = nil
258→                self.isDeleteInProgress = false
259→                IdleTimerManager.shared.enable()
260→                self.toastMessage = .filesDeleted(count)
261→            }
262→
263→            if !Task.isCancelled {
264→                await MainActor.run {
265→                    ShareSyncManager.shared.scheduleSync(vaultKey: key)
266→                }
267→            }
268→        }
269→    }
270→
271→    func batchExport() {
272→        guard let key = appState?.currentVaultKey else { return }
273→        let idsToExport = selectedIds
274→        let filesList = files
275→
276→        Task.detached(priority: .userInitiated) {
277→            let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
278→            var urls: [URL] = []
279→
280→            for id in idsToExport {
281→                guard !Task.isCancelled else { break }
282→                guard let result = try? VaultStorage.shared.retrieveFile(id: id, with: key) else { continue }
283→                let file = filesList.first { $0.id == id }
284→                let filename = file?.filename ?? "Export_\(id.uuidString)"
285→                let url = tempDir.appendingPathComponent(filename)
286→                try? result.content.write(to: url, options: [.atomic])
287→                urls.append(url)
288→            }
289→
290→            let finalizedURLs = urls
291→            await MainActor.run { [finalizedURLs] in
292→                guard !Task.isCancelled else { return }
293→                self.exportURLs = finalizedURLs
294→            }
295→        }
296→    }
297→
298→    func cleanupExportFiles() {
299→        for url in exportURLs {
300→            FileUtilities.cleanupTemporaryFile(at: url)
301→        }
302→        exportURLs = []
303→    }
304→
305→    func deleteFileById(_ id: UUID) {
306→        guard let key = appState?.currentVaultKey else { return }
307→        Task.detached(priority: .userInitiated) {
308→            do {
309→                try VaultStorage.shared.deleteFile(id: id, with: key)
310→                await MainActor.run {
311→                    if let idx = self.files.firstIndex(where: { $0.id == id }) {
312→                        self.files.remove(at: idx)
313→                    }
314→                    self.toastMessage = .filesDeleted(1)
315→                }
316→                
317→                // Trigger sync for shared vaults after deletion
318→                await MainActor.run {
319→                    ShareSyncManager.shared.scheduleSync(vaultKey: key)
320→                }
321→            } catch {
322→                await MainActor.run {
323→                    self.toastMessage = .error("Failed to delete file: \(error.localizedDescription)")
324→                }
325→            }
326→        }
327→    }
328→
329→    // MARK: - Import: Camera Capture
330→
331→    func handleCapturedImage(_ imageData: Data) {
332→        guard !isSharedVault, let key = appState?.currentVaultKey else { return }
333→        let currentMasterKey = self.masterKey
334→        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
335→
336→        Task.detached(priority: .userInitiated) {
337→            do {
338→                let tempInputURL = FileManager.default.temporaryDirectory
339→                    .appendingPathComponent(UUID().uuidString)
340→                    .appendingPathExtension("jpg")
341→                try imageData.write(to: tempInputURL, options: [.atomic])
342→
343→                let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
344→                    fileURL: tempInputURL, mimeType: "image/jpeg", mode: optimizationMode
345→                )
346→                defer {
347→                    if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
348→                    try? FileManager.default.removeItem(at: tempInputURL)
349→                }
350→
351→                let ext = mimeType == "image/heic" ? "heic" : "jpg"
352→                let filename = "IMG_\(Date().timeIntervalSince1970).\(ext)"
353→                let thumbnail = FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
354→
355→                let fileId = try VaultStorage.shared.storeFileFromURL(
356→                    optimizedURL, filename: filename, mimeType: mimeType,
357→                    with: key, thumbnailData: thumbnail
358→                )
359→                let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: currentMasterKey?.rawBytes ?? key.rawBytes) }
360→                if let encThumb {
361→                    await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
362→                }
363→
364→                EmbraceManager.shared.trackFeatureUsage("camera_capture", context: [
365→                    "optimized": String(wasOptimized),
366→                    "mime_type": mimeType
367→                ])
368→
369→                await MainActor.run {
370→                    if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count + 1) {
371→                        self.toastMessage = .milestone(milestone)
372→                    } else {
373→                        self.toastMessage = .fileEncrypted()
374→                    }
375→                    self.loadFiles()
376→                }
377→
378→                await ShareSyncManager.shared.scheduleSync(vaultKey: key)
379→            } catch {
380→                await MainActor.run {
381→                    self.toastMessage = .importFailed(1, imported: 0, reason: error.localizedDescription)
382→                }
383→            }
384→        }
385→    }
386→
387→    // MARK: - Import: Photo Picker
388→
389→    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
390→    func handleSelectedPhotos(_ results: [PHPickerResult]) -> (remaining: Int, selected: Int)? {
391→        vmLogger.info("📸 handleSelectedPhotos called with \(results.count) results")
392→        guard !isSharedVault, appState?.currentVaultKey != nil else {
393→            vmLogger.warning("❌ handleSelectedPhotos: guard failed - isSharedVault=\(self.isSharedVault), hasKey=\(self.appState?.currentVaultKey != nil)")
394→            return nil
395→        }
396→        guard let subscriptionManager else {
397→            vmLogger.warning("❌ handleSelectedPhotos: no subscriptionManager")
398→            return nil
399→        }
400→
401→        if !subscriptionManager.isPremium {
402→            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - self.files.count)
403→            vmLogger.info("💰 Free tier check: remaining=\(remaining), files.count=\(self.files.count)")
404→            if remaining == 0 {
405→                return (0, results.count)
406→            }
407→            if results.count > remaining {
408→                pendingImport = .photos(results)
409→                return (remaining, results.count)
410→            }
411→        }
412→
413→        vmLogger.info("✅ Proceeding with photo import of \(results.count) photos")
414→        performPhotoImport(results)
415→        return nil
416→    }
417→
418→    func performPhotoImport(_ results: [PHPickerResult]) {
419→        vmLogger.info("performPhotoImport START with \(results.count) photos")
420→        guard let key = appState?.currentVaultKey else {
421→            vmLogger.error("performPhotoImport: no vault key")
422→            return
423→        }
424→        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
425→        let count = results.count
426→        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
427→
428→        activeImportTask?.cancel()
429→        importGeneration += 1
430→        let myGeneration = importGeneration
431→        importProgress = (0, count)
432→        IdleTimerManager.shared.disable()
433→
434→        // Separate videos and images for prioritized parallel processing:
435→        // 1 dedicated video worker + 2 image workers = 3 parallel workers
436→        var videoWork: [ParallelImporter.PickerWorkItem] = []
437→        var imageWork: [ParallelImporter.PickerWorkItem] = []
438→
439→        for (index, result) in results.enumerated() {
440→            let isVideo = result.itemProvider.hasItemConformingToTypeIdentifier(UTType.movie.identifier)
441→            let item = ParallelImporter.PickerWorkItem(originalIndex: index, provider: result.itemProvider)
442→            if isVideo { videoWork.append(item) } else { imageWork.append(item) }
443→        }
444→
445→        let videoWorkerCount = videoWork.isEmpty ? 0 : 1
446→        let imageWorkerCount = videoWork.isEmpty ? 3 : 2
447→        vmLogger.info("Import: \(videoWork.count) videos, \(imageWork.count) images — \(videoWorkerCount) video + \(imageWorkerCount) image workers")
448→
449→        let (stream, continuation) = AsyncStream<ParallelImporter.ImportEvent>.makeStream()
450→
451→        activeImportTask = Task(priority: .userInitiated) { [weak self] in
452→            guard let self else { return }
453→
454→            // Launch parallel workers off MainActor
455→            let workerTask = Task.detached(priority: .userInitiated) {
456→                await ParallelImporter.runPhotoImport(
457→                    videoWork: videoWork,
458→                    imageWork: imageWork,
459→                    videoWorkerCount: videoWorkerCount,
460→                    imageWorkerCount: imageWorkerCount,
461→                    key: key,
462→                    encryptionKey: encryptionKey,
463→                    optimizationMode: optimizationMode,
464→                    continuation: continuation
465→                )
466→                continuation.finish()
467→            }
468→
469→            // Consume events on MainActor for real-time UI updates
470→            var successCount = 0
471→            var failedCount = 0
472→            var lastErrorReason: String?
473→
474→            for await event in stream {
475→                guard !Task.isCancelled else { break }
476→
477→                switch event {
478→                case .imported(let file):
479→                    successCount += 1
480→                    self.files.append(file)
481→                case .failed(let reason):
482→                    failedCount += 1
483→                    if let reason { lastErrorReason = reason }
484→                }
485→                self.importProgress = (successCount + failedCount, count)
486→            }
487→
488→            // Cancel workers if we were cancelled
489→            if Task.isCancelled {
490→                workerTask.cancel()
491→                vmLogger.warning("Photo import cancelled (imported=\(successCount), failed=\(failedCount))")
492→            }
493→
494→            // Only clean up state if we're still the current import — a newer import
495→            // may have already set its own importProgress and disabled IdleTimerManager
496→            guard self.importGeneration == myGeneration else { return }
497→
498→            self.importProgress = nil
499→            IdleTimerManager.shared.enable()
500→
501→            guard !Task.isCancelled else { return }
502→
503→            vmLogger.info("Import complete — success: \(successCount), failed: \(failedCount), files.count: \(self.files.count)")
504→
505→            if failedCount > 0 && successCount == 0 {
506→                self.toastMessage = .importFailed(failedCount, imported: 0, reason: lastErrorReason)
507→            } else if failedCount > 0 {
508→                self.toastMessage = .importFailed(failedCount, imported: successCount, reason: lastErrorReason)
509→            } else if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count) {
510→                self.toastMessage = .milestone(milestone)
511→            } else {
512→                self.toastMessage = .filesImported(successCount)
513→            }
514→
515→            if successCount > 0 {
516→                self.updateFilterAfterImport()
517→                ShareSyncManager.shared.scheduleSync(vaultKey: key)
518→            }
519→        }
520→    }
521→
522→    // MARK: - Import: File Importer
523→
524→    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
525→    func handleImportedFiles(_ result: Result<[URL], Error>) -> (remaining: Int, selected: Int)? {
526→        guard !isSharedVault, appState?.currentVaultKey != nil else { return nil }
527→        guard case .success(let urls) = result else { return nil }
528→        guard let subscriptionManager else { return nil }
529→
530→        if !subscriptionManager.isPremium {
531→            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - files.count)
532→            if remaining == 0 {
533→                return (0, urls.count)
534→            }
535→            if urls.count > remaining {
536→                pendingImport = .files(urls)
537→                return (remaining, urls.count)
538→            }
539→        }
540→
541→        performFileImport(urls)
542→        return nil
543→    }
544→
545→    func performFileImport(_ urls: [URL]) {
546→        guard let key = appState?.currentVaultKey else { return }
547→        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
548→        let count = urls.count
549→        let showProgress = count > 1
550→        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
551→
552→        activeImportTask?.cancel()
553→        importGeneration += 1
554→        let myGeneration = importGeneration
555→        if showProgress { importProgress = (0, count) }
556→        IdleTimerManager.shared.disable()
557→
558→        // Separate videos and non-videos for prioritized parallel processing
559→        var videoWork: [ParallelImporter.URLWorkItem] = []
560→        var otherWork: [ParallelImporter.URLWorkItem] = []
561→
562→        for (index, url) in urls.enumerated() {
563→            let mime = FileUtilities.mimeType(forExtension: url.pathExtension)
564→            let item = ParallelImporter.URLWorkItem(originalIndex: index, url: url)
565→            if mime.hasPrefix("video/") { videoWork.append(item) } else { otherWork.append(item) }
566→        }
567→
568→        let videoWorkerCount = videoWork.isEmpty ? 0 : 1
569→        let otherWorkerCount = videoWork.isEmpty ? 3 : 2
570→        vmLogger.info("File import: \(videoWork.count) videos, \(otherWork.count) other — \(videoWorkerCount + otherWorkerCount) workers")
571→
572→        let (stream, continuation) = AsyncStream<ParallelImporter.ImportEvent>.makeStream()
573→
574→        activeImportTask = Task(priority: .userInitiated) { [weak self] in
575→            guard let self else { return }
576→
577→            let workerTask = Task.detached(priority: .userInitiated) {
578→                await ParallelImporter.runFileImport(
579→                    videoWork: videoWork,
580→                    otherWork: otherWork,
581→                    videoWorkerCount: videoWorkerCount,
582→                    otherWorkerCount: otherWorkerCount,
583→                    key: key,
584→                    encryptionKey: encryptionKey,
585→                    optimizationMode: optimizationMode,
586→                    continuation: continuation
587→                )
588→                continuation.finish()
589→            }
590→
591→            var successCount = 0
592→            var failedCount = 0
593→            var lastErrorReason: String?
594→
595→            for await event in stream {
596→                guard !Task.isCancelled else { break }
597→
598→                switch event {
599→                case .imported(let file):
600→                    successCount += 1
601→                    self.files.append(file)
602→                case .failed(let reason):
603→                    failedCount += 1
604→                    if let reason { lastErrorReason = reason }
605→                }
606→                if showProgress {
607→                    self.importProgress = (successCount + failedCount, count)
608→                }
609→            }
610→
611→            if Task.isCancelled {
612→                workerTask.cancel()
613→                vmLogger.warning("File import cancelled (imported=\(successCount), failed=\(failedCount))")
614→            }
615→
616→            // Only clean up state if we're still the current import
617→            guard self.importGeneration == myGeneration else { return }
618→
619→            self.importProgress = nil
620→            IdleTimerManager.shared.enable()
621→
622→            guard !Task.isCancelled else { return }
623→
624→            if failedCount > 0 && successCount == 0 {
625→                self.toastMessage = .importFailed(failedCount, imported: 0, reason: lastErrorReason)
626→            } else if failedCount > 0 {
627→                self.toastMessage = .importFailed(failedCount, imported: successCount, reason: lastErrorReason)
628→            } else {
629→                self.toastMessage = .filesImported(successCount)
630→            }
631→
632→            vmLogger.info("File import complete: imported=\(successCount), failed=\(failedCount)")
633→            if successCount > 0 {
634→                self.updateFilterAfterImport()
635→                ShareSyncManager.shared.scheduleSync(vaultKey: key)
636→            }
637→        }
638→    }
639→
640→    // MARK: - Import: Pending Files
641→
642→    func importPendingFiles() {
643→        guard !isImportingPendingFiles else { return }
644→        guard let vaultKey = appState?.currentVaultKey else { return }
645→
646→        isImportingPendingFiles = true
647→        IdleTimerManager.shared.disable()
648→
649→        let fingerprint = KeyDerivation.keyFingerprint(from: vaultKey.rawBytes)
650→        let initialPendingCount = StagedImportManager.pendingImportableFileCount(for: fingerprint)
651→        if initialPendingCount > 0 {
652→            importProgress = (0, initialPendingCount)
653→        }
654→
655→        activeImportTask?.cancel()
656→        activeImportTask = Task.detached(priority: .userInitiated) {
657→            let result = await ImportIngestor.processPendingImports(for: vaultKey) { progress in
658→                await MainActor.run {
659→                    guard !Task.isCancelled else { return }
660→                    self.importProgress = (progress.completed, progress.total)
661→                }
662→            }
663→
664→            await MainActor.run {
665→                self.isImportingPendingFiles = false
666→                self.importProgress = nil
667→                IdleTimerManager.shared.enable()
668→            }
669→
670→            guard !Task.isCancelled else { return }
671→
672→            let remainingImportable = StagedImportManager.pendingImportableFileCount(for: fingerprint)
673→            await MainActor.run {
674→                guard let appState = self.appState else { return }
675→                appState.hasPendingImports = remainingImportable > 0
676→                appState.pendingImportCount = remainingImportable
677→
678→                if result.imported > 0 {
679→                    self.loadFiles()
680→                    let generator = UINotificationFeedbackGenerator()
681→                    generator.notificationOccurred(.success)
682→                    if result.failed > 0 {
683→                        self.toastMessage = .importFailed(result.failed, imported: result.imported, reason: result.failureReason)
684→                    } else {
685→                        self.toastMessage = .filesImported(result.imported)
686→                    }
687→                } else if result.failed > 0 {
688→                    self.toastMessage = .importFailed(result.failed, imported: 0, reason: result.failureReason)
689→                    let generator = UINotificationFeedbackGenerator()
690→                    generator.notificationOccurred(.error)
691→                } else {
692→                    appState.hasPendingImports = false
693→                    appState.pendingImportCount = 0
694→                }
695→            }
696→        }
697→    }
698→
699→    func proceedWithLimitedImport(limitAlertRemaining: Int) {
700→        guard let pending = pendingImport else { return }
701→        let remaining = limitAlertRemaining
702→        pendingImport = nil
703→
704→        switch pending {
705→        case .photos(let results):
706→            performPhotoImport(Array(results.prefix(remaining)))
707→        case .files(let urls):
708→            performFileImport(Array(urls.prefix(remaining)))
709→        }
710→    }
711→
712→    // MARK: - Shared Vault
713→
714→    func checkSharedVaultStatus() {
715→        guard let key = appState?.currentVaultKey else { return }
716→
717→        Task {
718→            do {
719→                var index = try VaultStorage.shared.loadIndex(with: key)
720→
721→                let shared = index.isSharedVault ?? false
722→                await MainActor.run {
723→                    isSharedVault = shared
724→                    sharePolicy = index.sharePolicy
725→                    sharedVaultId = index.sharedVaultId
726→                }
727→
728→                guard shared else { return }
729→
730→                EmbraceManager.shared.trackFeatureUsage("shared_vault_opened", context: [
731→                    "vault_id": sharedVaultId ?? "unknown",
732→                    "has_expiration": String(index.sharePolicy?.expiresAt != nil),
733→                    "has_max_opens": String(index.sharePolicy?.maxOpens != nil)
734→                ])
735→
736→                if let expires = index.sharePolicy?.expiresAt, Date() > expires {
737→                    await MainActor.run {
738→                        selfDestructMessage = "This shared vault has expired. The vault owner set an expiration date of \(expires.formatted(date: .abbreviated, time: .omitted)). All shared files have been removed."
739→                        showSelfDestructAlert = true
740→                    }
741→                    return
742→                }
743→
744→                if !hasCountedOpenThisSession {
745→                    let currentOpens = (index.openCount ?? 0) + 1
746→                    if let maxOpens = index.sharePolicy?.maxOpens, currentOpens > maxOpens {
747→                        await MainActor.run {
748→                            selfDestructMessage = "This shared vault has reached its maximum number of opens. All shared files have been removed."
749→                            showSelfDestructAlert = true
750→                        }
751→                        return
752→                    }
753→
754→                    index.openCount = currentOpens
755→                    try VaultStorage.shared.saveIndex(index, with: key)
756→                    await MainActor.run { hasCountedOpenThisSession = true }
757→                }
758→
759→                if let vaultId = index.sharedVaultId {
760→                    do {
761→                        let currentVersion = index.sharedVaultVersion ?? 1
762→                        if let _ = try await CloudKitSharingManager.shared.checkForUpdates(
763→                            shareVaultId: vaultId, currentVersion: currentVersion
764→                        ) {
765→                            await MainActor.run {
766→                                updateAvailable = true
767→                            }
768→                        }
769→                    } catch CloudKitSharingError.revoked {
770→                        await MainActor.run {
771→                            selfDestructMessage = "The vault owner has revoked your access to this shared vault. All shared files have been removed."
772→                            showSelfDestructAlert = true
773→                        }
774→                    } catch {
775→                        #if DEBUG
776→                        print("⚠️ [VaultViewModel] Failed to check for updates: \(error)")
777→                        #endif
778→                    }
779→                }
780→            } catch {
781→                #if DEBUG
782→                print("❌ [VaultViewModel] Failed to check shared vault status: \(error)")
783→                #endif
784→            }
785→        }
786→    }
787→
788→    func selfDestruct() {
789→        guard let key = appState?.currentVaultKey else { return }
790→
791→        do {
792→            let index = try VaultStorage.shared.loadIndex(with: key)
793→
794→            if let vaultId = index.sharedVaultId {
795→                Task {
796→                    do {
797→                        try await CloudKitSharingManager.shared.markShareConsumed(shareVaultId: vaultId)
798→                    } catch {
799→                        vmLogger.error("Failed to mark share consumed during self-destruct: \(error.localizedDescription, privacy: .public)")
800→                        EmbraceManager.shared.captureError(error, context: ["action": "markShareConsumed", "vaultId": vaultId])
801→                    }
802→                }
803→            }
804→
805→            for file in index.files where !file.isDeleted {
806→                do {
807→                    try VaultStorage.shared.deleteFile(id: file.fileId, with: key)
808→                } catch {
809→                    vmLogger.error("Failed to delete file during self-destruct \(file.fileId, privacy: .public): \(error.localizedDescription, privacy: .public)")
810→                    EmbraceManager.shared.captureError(error, context: ["action": "deleteFile", "fileId": file.fileId])
811→                }
812→            }
813→            try VaultStorage.shared.deleteVaultIndex(for: key)
814→        } catch {
815→            vmLogger.error("Self-destruct error: \(error.localizedDescription, privacy: .public)")
816→            EmbraceManager.shared.captureError(error, context: ["action": "selfDestruct"])
817→        }
818→
819→        appState?.lockVault()
820→    }
821→
822→    // MARK: - Vault Key Change
823→
824→    func handleVaultKeyChange(oldKey: VaultKey?, newKey: VaultKey?) {
825→        if activeImportTask != nil {
826→            vmLogger.warning("Vault key changed during active import — cancelling import (files.count=\(self.files.count))")
827→        }
828→        activeImportTask?.cancel()
829→        activeImportTask = nil
830→        importProgress = nil
831→        isDeleteInProgress = false
832→        IdleTimerManager.shared.enable()
833→
834→        if oldKey != newKey {
835→            vmLogger.info("Vault key changed: clearing files (had \(self.files.count) files)")
836→            files = []
837→            masterKey = nil
838→            Task { await ThumbnailCache.shared.clear() }
839→            isLoading = false
840→            isSharedVault = false
841→        }
842→
843→        if newKey != nil {
844→            ShareUploadManager.shared.resumePendingUploadsIfNeeded(trigger: "vault_key_changed")
845→        }
846→    }
847→
848→    // MARK: - Filter Helpers
849→
850→    func updateFilterAfterImport() {
851→        let hasNonMedia = files.contains { !$0.isMedia }
852→        if hasNonMedia && fileFilter == .media {
853→            setFileFilter(.all)
854→        } else if !hasNonMedia && fileFilter != .media {
855→            setFileFilter(.media)
856→        }
857→    }
858→}
