694→    /// Formats a date as relative time (e.g., "5 minutes ago", "1 day ago")
695→    private func relativeTimeString(from date: Date) -> String {
696→        let now = Date()
697→        let diff = now.timeIntervalSince(date)
698→        
699→        // Less than a minute
700→        if diff < 60 {
701→            return "just now"
702→        }
703→        // Less than an hour - show minutes
704→        else if diff < 3600 {
705→            let minutes = Int(diff / 60)
706→            return minutes == 1 ? "1 minute ago" : "\(minutes) minutes ago"
707→        }
708→        // Less than a day - show hours
709→        else if diff < 86400 {
710→            let hours = Int(diff / 3600)
711→            return hours == 1 ? "1 hour ago" : "\(hours) hours ago"
712→        }
713→        // Less than 30 days - show days
714→        else if diff < 2592000 {
715→            let days = Int(diff / 86400)
716→            return days == 1 ? "1 day ago" : "\(days) days ago"
717→        }
718→        // More than 30 days - show the date
719→        else {
720→            let formatter = DateFormatter()
721→            formatter.dateStyle = .medium
722→            formatter.timeStyle = .none
723→            return formatter.string(from: date)
724→        }
725→    }
