727→    private func revokeShare(_ share: VaultStorage.ShareRecord) async {
728→        guard let key = appState.currentVaultKey else { return }
729→
730→        // Update local state first for instant UI response
731→        activeShares.removeAll { $0.id == share.id }
732→        
733→        // Check if this was the last share - if so, terminate any ongoing sync
734→        let remainingShares = activeShares
735→        let remainingJobs = uploadJobs.filter { $0.shareVaultId != share.id }
736→        
737→        if remainingShares.isEmpty && remainingJobs.isEmpty {
738→            // No more users - terminate any ongoing uploads for this specific share
739→            for job in uploadJobs where job.shareVaultId == share.id && job.canTerminate {
740→                ShareUploadManager.shared.terminateUpload(
741→                    jobId: job.id,
742→                    vaultKey: key,
743→                    cleanupRemote: true
744→                )
745→            }
746→            uploadJobs.removeAll { $0.shareVaultId == share.id }
747→            mode = .newShare
748→        }
749→        // If there are remaining shares, sync will continue for those users
750→
751→        do {
752→            // Persist to index
753→            var index = try await VaultStorage.shared.loadIndex(with: key)
754→            index.activeShares?.removeAll { $0.id == share.id }
755→            try await VaultStorage.shared.saveIndex(index, with: key)
756→        } catch {
757→            shareVaultLogger.error("Failed to update index after revoke: \(error.localizedDescription, privacy: .public)")
758→        }
759→
760→        // Delete from CloudKit in background (fire-and-forget)
761→        Task {
762→            try? await CloudKitSharingManager.shared.revokeShare(shareVaultId: share.id)
763→        }
764→    }
765→
766→    private func stopAllSharing() async {
767→        guard let key = appState.currentVaultKey else { return }
768→
769→        isStopping = true
770→
771→        do {
772→            // Cancel all upload jobs for this vault first
773→            for job in uploadJobs where job.canTerminate {
774→                ShareUploadManager.shared.terminateUpload(
775→                    jobId: job.id,
776→                    vaultKey: key,
777→                    cleanupRemote: true
778→                )
779→            }
780→
781→            // Immediately clear local shares so UI feels responsive
782→            var index = try await VaultStorage.shared.loadIndex(with: key)
783→            let sharesToDelete = index.activeShares ?? []
784→            index.activeShares = nil
785→            try await VaultStorage.shared.saveIndex(index, with: key)
786→
787→            activeShares = []
788→            uploadJobs = []
789→            mode = .newShare
790→            isStopping = false
791→
792→            // Delete from CloudKit in background
793→            for share in sharesToDelete {
794→                try? await CloudKitSharingManager.shared.deleteSharedVault(shareVaultId: share.id)
795→            }
796→        } catch {
797→            isStopping = false
798→            mode = .error("Failed to stop sharing: \(error.localizedDescription)")
799→        }
800→    }
