802→    private func terminateUpload(_ job: ShareUploadManager.UploadJob) {
803→        ShareUploadManager.shared.terminateUpload(
804→            jobId: job.id,
805→            vaultKey: appState.currentVaultKey,
806→            cleanupRemote: true
807→        )
808→
809→        uploadJobs.removeAll { $0.id == job.id }
810→        activeShares.removeAll { $0.id == job.shareVaultId }
811→        if uploadJobs.isEmpty && activeShares.isEmpty {
812→            mode = .newShare
813→        }
814→    }
