848→    private struct LocalSnapshot: Sendable {
849→        let estimatedUploadSize: Int
850→        let activeShares: [VaultStorage.ShareRecord]
851→    }
852→
853→    private static func loadLocalSnapshot(vaultKey: VaultKey) async throws -> LocalSnapshot {
854→        try await Task.detached(priority: .userInitiated) {
855→            let index = try await VaultStorage.shared.loadIndex(with: vaultKey)
856→            let estimatedSize = index.files.filter { !$0.isDeleted }.reduce(0) { $0 + $1.size }
857→            let shares = index.activeShares ?? []
858→            // Sort by creation date, newest first (descending order)
859→            let sortedShares = shares.sorted { $0.createdAt > $1.createdAt }
860→            return LocalSnapshot(
861→                estimatedUploadSize: estimatedSize,
862→                activeShares: sortedShares
863→            )
864→        }.value
865→    }
866→
867→    private static func loadActiveShares(vaultKey: VaultKey) async throws -> [VaultStorage.ShareRecord] {
868→        try await Task.detached(priority: .utility) {
869→            let index = try await VaultStorage.shared.loadIndex(with: vaultKey)
870→            let shares = index.activeShares ?? []
871→            // Sort by creation date, newest first
872→            return shares.sorted { $0.createdAt > $1.createdAt }
873→        }.value
874→    }
