876→    private func reconcileConsumedShares(
877→        vaultKey: VaultKey,
878→        initialShares: [VaultStorage.ShareRecord],
879→        generation: Int
880→    ) async {
881→        guard !initialShares.isEmpty else { return }
882→
883→        let consumedMap: [String: Bool]
884→        do {
885→            consumedMap = try await CloudKitSharingManager.shared.consumedStatusByShareVaultIds(initialShares.map(\.id))
886→        } catch {
887→            shareVaultLogger.warning("Failed to check consumed status: \(error.localizedDescription, privacy: .private)")
888→            return
889→        }
890→        let consumedIds = Set(consumedMap.compactMap { $0.value ? $0.key : nil })
891→        guard !consumedIds.isEmpty else { return }
892→
893→        do {
894→            let updatedShares = try await Task.detached(priority: .utility) { () -> [VaultStorage.ShareRecord] in
895→                var index = try await VaultStorage.shared.loadIndex(with: vaultKey)
896→                var shares = index.activeShares ?? []
897→                shares.removeAll { consumedIds.contains($0.id) }
898→                // Sort by creation date, newest first (descending order)
899→                shares.sort { $0.createdAt > $1.createdAt }
900→                index.activeShares = shares.isEmpty ? nil : shares
901→                try await VaultStorage.shared.saveIndex(index, with: vaultKey)
902→                return shares
903→            }.value
904→
905→            await MainActor.run {
906→                guard generation == initializationGeneration else { return }
907→                activeShares = updatedShares
908→                updateModeForCurrentData()
909→            }
910→        } catch {
911→            shareVaultLogger.error("Failed to reconcile consumed shares: \(error.localizedDescription, privacy: .public)")
912→        }
913→    }
914→
915→    static func shouldDisplayUploadJob(_ job: ShareUploadManager.UploadJob) -> Bool {
916→        if case .complete = job.status { return false }
917→        if case .cancelled = job.status { return false }
918→        return true
919→    }
920→
921→    static func shouldDisableIdleTimer(
922→        isShareScreenVisible: Bool,
923→        uploadJobs: [ShareUploadManager.UploadJob]
924→    ) -> Bool {
925→        guard isShareScreenVisible else { return false }
926→        return uploadJobs.contains(where: { $0.status.isRunning })
927→            || ShareSyncManager.shared.syncStatus == .syncing
928→    }
929→
930→    private func iCloudStatusMessage(_ status: CKAccountStatus) -> String {
931→        switch status {
932→        case .available: return "iCloud is available"
933→        case .noAccount: return "Please sign in to iCloud in Settings"
934→        case .restricted: return "iCloud access is restricted"
935→        case .couldNotDetermine: return "Could not determine iCloud status"
936→        case .temporarilyUnavailable: return "iCloud is temporarily unavailable. Please check your iCloud settings and try again."
937→        @unknown default: return "iCloud is not available"
938→        }
939→    }
940→}
941→
942→private struct SharePhraseSheetItem: Identifiable {
943→    let phrase: String
944→    var id: String { phrase }
945→}
946→
