private func makeShareRecord(
    id: String = UUID().uuidString,
    shareKeyData: Data? = Data(repeating: 0xCC, count: 32)
) -> VaultStorage.ShareRecord {
    VaultStorage.ShareRecord(
        id: id,
        createdAt: Date(),
        policy: VaultStorage.SharePolicy(),
        lastSyncedAt: nil,
        shareKeyData: shareKeyData,
        syncSequence: nil
    )
}

