            Text(message)
                .foregroundStyle(.vaultSecondaryText).multilineTextAlignment(.center)
                .lineLimit(nil)
            Button("Try Again") { mode = .newShare }
                .vaultProminentButtonStyle().padding(.top)
        }
        .padding(.top, 60)
