  1→import SwiftUI
  2→import os.log
  3→
  4→private let logger = Logger(subsystem: "app.vaultaire.ios", category: "ContentView")
  5→
  6→struct ContentView: View {
  7→    @Environment(AppState.self) private var appState
  8→    @Environment(DeepLinkHandler.self) private var deepLinkHandler
  9→    @Environment(\.accessibilityReduceMotion) private var reduceMotion
 10→
 11→    @State private var showUnlockTransition = false
 12→
 13→    var body: some View {
 14→        ZStack {
 15→            // Static background: correct from frame 1, trait-independent.
 16→            appState.launchBackgroundColor
 17→                .ignoresSafeArea()
 18→
 19→            Group {
 20→                if appState.showOnboarding {
 21→                    OnboardingView()
 22→                } else if appState.isLoading {
 23→                    LoadingView()
 24→                } else if appState.isUnlocked {
 25→                    VaultView()
 26→                } else {
 27→                    PatternLockView()
 28→                }
 29→            }
 30→            #if DEBUG
 31→            .onAppear {
 32→                if appState.isMaestroTestMode && !appState.isUnlocked && !appState.showOnboarding {
 33→                    appState.maestroTestUnlock()
 34→                }
 35→            }
 36→            #endif
 37→
 38→            // Vault door unlock overlay — always present, opacity-controlled.
 39→            // Using direct opacity instead of conditional insertion avoids
 40→            // a timing issue where the overlay never reaches full opacity.
 41→            Color.vaultBackground
 42→                .ignoresSafeArea()
 43→                .overlay {
 44→                    Image(systemName: "shield.fill")
 45→                        .font(.system(size: 64))
 46→                        .foregroundStyle(Color.accentColor)
 47→                        .scaleEffect(showUnlockTransition ? 1.2 : 1.0)
 48→                }
 49→                .opacity(showUnlockTransition ? 1 : 0)
 50→                .allowsHitTesting(showUnlockTransition)
 51→
 52→            // Screenshot detected: full-screen overlay (covers UI before lock)
 53→            if appState.screenshotDetected {
 54→                Color.vaultBackground
 55→                    .ignoresSafeArea()
 56→            }
 57→        }
 58→        .fullScreenCover(isPresented: Binding(
 59→            get: { deepLinkHandler.pendingSharePhrase != nil },
 60→            set: { if !$0 { deepLinkHandler.clearPending() } }
 61→        )) {
 62→            SharedVaultInviteView()
 63→                .environment(appState)
 64→                .environment(deepLinkHandler)
 65→                .environment(SubscriptionManager.shared)
 66→        }
 67→        .ignoresSafeArea(.keyboard)
 68→        .onChange(of: appState.isUnlocked) { _, isUnlocked in
 69→            guard isUnlocked else {
 70→                // Safety: ensure unlock overlay is dismissed when vault locks,
 71→                // even if the fade-out animation was interrupted.
 72→                showUnlockTransition = false
 73→                return
 74→            }
 75→
 76→            // Trigger silent background backup if enabled and overdue
 77→            if let key = appState.currentVaultKey {
 78→                iCloudBackupManager.shared.performBackupIfNeeded(with: key)
 79→            }
 80→
 81→            guard !reduceMotion else { return }
 82→            showUnlockTransition = true
 83→            // Delay one frame so the overlay renders at full opacity before fading.
 84→            DispatchQueue.main.async {
 85→                withAnimation(.easeOut(duration: 0.9)) {
 86→                    showUnlockTransition = false
 87→                }
 88→            }
 89→        }
 90→        // Safety: when the app becomes active, clear any stuck overlays.
 91→        // Covers edge cases where background suspension interrupts animations
 92→        // or leaves screenshotDetected stuck.
 93→        .onReceive(NotificationCenter.default.publisher(for: UIApplication.didBecomeActiveNotification)) { _ in
 94→            if !appState.isUnlocked {
 95→                showUnlockTransition = false
 96→            }
 97→            if appState.screenshotDetected && !appState.isUnlocked {
 98→                appState.screenshotDetected = false
 99→            }
100→        }
101→        // Screenshot detection — locks vault when user takes a screenshot
102→        .onReceive(NotificationCenter.default.publisher(for: UIApplication.userDidTakeScreenshotNotification)) { _ in
103→            #if DEBUG
104→            if appState.isMaestroTestMode { return }
105→            #endif
106→            logger.info("Screenshot detected, locking vault")
107→            EmbraceManager.shared.addBreadcrumb(category: "app.locked", data: ["trigger": "screenshot"])
108→            appState.screenshotDetected = true
109→            Task {
110→                try? await Task.sleep(nanoseconds: 100_000_000) // 0.1s
111→                appState.lockVault()
112→            }
113→        }
114→        // Screen recording detection
115→        .onReceive(NotificationCenter.default.publisher(for: UIScreen.capturedDidChangeNotification)) { notification in
116→            #if DEBUG
117→            if appState.isMaestroTestMode { return }
118→            #endif
119→            guard let screen = notification.object as? UIScreen else { return }
120→            if screen.isCaptured {
121→                logger.info("Screen recording detected, locking vault")
122→                EmbraceManager.shared.addBreadcrumb(category: "app.locked", data: ["trigger": "recording"])
123→                appState.lockVault()
124→            }
125→        }
126→        // Lock only when the app actually enters background.
127→        // `willResignActive` also fires for transient system UI (e.g. import pickers),
128→        // which can interrupt in-app file import flows.
129→        .onReceive(NotificationCenter.default.publisher(for: UIApplication.didEnterBackgroundNotification)) { _ in
130→            #if DEBUG
131→            if appState.isMaestroTestMode { return }
132→            #endif
133→            if appState.suppressLockForShareSheet { return }
134→            logger.debug("App entered background, locking vault")
135→            EmbraceManager.shared.addBreadcrumb(category: "app.locked", data: ["trigger": "background"])
136→            appState.lockVault()
137→        }
138→        #if DEBUG
139→        // Simulator: Cmd+S doesn't post userDidTakeScreenshotNotification.
140→        // Shake device (Ctrl+Cmd+Z in simulator) to simulate a screenshot for testing.
141→        .onReceive(NotificationCenter.default.publisher(for: UIDevice.deviceDidShakeNotification)) { _ in
142→            logger.debug("Simulated screenshot via shake")
143→            appState.screenshotDetected = true
144→            Task {
145→                try? await Task.sleep(nanoseconds: 100_000_000)
146→                appState.lockVault()
147→            }
148→        }
149→        #endif
150→    }
151→}
152→
153→#if DEBUG
154→// Extend UIDevice to detect shake gestures for debug screenshot simulation
155→extension UIDevice {
156→    static let deviceDidShakeNotification = Notification.Name("deviceDidShakeNotification")
157→}
158→
159→extension UIWindow {
160→    open override func motionEnded(_ motion: UIEvent.EventSubtype, with event: UIEvent?) {
161→        super.motionEnded(motion, with: event)
162→        if motion == .motionShake {
163→            NotificationCenter.default.post(name: UIDevice.deviceDidShakeNotification, object: nil)
164→        }
165→    }
166→}
167→#endif
168→
169→#Preview {
170→    ContentView()
171→        .environment(AppState())
172→        .environment(SubscriptionManager.shared)
173→}
