        let vault = VaultScreen(app: app)
        XCTAssertTrue(vault.isDisplayed)

        let settings = vault.tapSettings()
        XCTAssertTrue(settings.isDisplayed)

        let changePattern = settings.tapChangePattern()
        XCTAssertTrue(changePattern.isDisplayed)

        // Skip verify step (debug button in test builds)
        changePattern.skipVerify()

        // Draw new pattern
        changePattern.drawPattern()

        // Confirm new pattern
        changePattern.confirmPattern()

        // Recovery phrase should be shown — tap "I've saved it"
        XCTAssertTrue(changePattern.recoverySavedButton.waitForExistence(timeout: 10))
        let settingsAgain = changePattern.tapRecoverySaved()
        XCTAssertTrue(settingsAgain.isDisplayed)
    }

