 247→    private func setAsDuressVault() {
 248→        guard let key = appState.currentVaultKey else { return }
 249→        EmbraceManager.shared.addBreadcrumb(category: "settings.duressToggled")
 250→        Task {
 251→            do {
 252→                try await DuressHandler.shared.setAsDuressVault(key: key)
 253→            } catch {
 254→                EmbraceManager.shared.captureError(error)
 255→            }
 256→        }
 257→    }
