 266→    private func deleteVault() {
 267→        guard let key = appState.currentVaultKey else {
 268→            dismiss()
 269→            return
 270→        }
 271→
 272→        // Delete all files and the vault index
 273→        do {
 274→            let index = try VaultStorage.shared.loadIndex(with: key)
 275→            for file in index.files where !file.isDeleted {
 276→                try? VaultStorage.shared.deleteFile(id: file.fileId, with: key)
 277→            }
 278→
 279→            // Remove any active CloudKit shares
 280→            if let shares = index.activeShares {
 281→                for share in shares {
 282→                    Task {
 283→                        try? await CloudKitSharingManager.shared.deleteSharedVault(shareVaultId: share.id)
 284→                    }
 285→                }
 286→            }
 287→
 288→            try VaultStorage.shared.deleteVaultIndex(for: key)
 289→        } catch {
 290→            vaultSettingsLogger.error("Delete vault error: \(error.localizedDescription, privacy: .public)")
 291→        }
 292→
 293→        // Clean up recovery data and duress status
 294→        Task {
 295→            try? await RecoveryPhraseManager.shared.deleteRecoveryData(for: key)
 296→            if await DuressHandler.shared.isDuressKey(key) {
 297→                await DuressHandler.shared.clearDuressVault()
 298→            }
 299→        }
 300→
 301→        appState.lockVault()
 302→        dismiss()
 303→    }
