 305→    private func regenerateRecoveryPhrase() {
 306→        guard let key = appState.currentVaultKey else { return }
 307→        EmbraceManager.shared.addBreadcrumb(category: "settings.phraseRegenerated")
 308→        Task {
 309→            do {
 310→                do {
 311→                    _ = try await RecoveryPhraseManager.shared.regenerateRecoveryPhrase(for: key)
 312→                } catch RecoveryError.vaultNotFound {
 313→                    // No existing recovery data — create fresh entry
 314→                    vaultSettingsLogger.info("No recovery data found, creating new recovery phrase")
 315→                    let newPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
 316→                    try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
 317→                        phrase: newPhrase,
 318→                        pattern: appState.currentPattern ?? [],
 319→                        gridSize: 5,
 320→                        patternKey: key
 321→                    )
 322→                }
 323→                vaultSettingsLogger.debug("Recovery phrase regenerated")
 324→                await MainActor.run {
 325→                    showingRegeneratedPhrase = true
 326→                }
 327→            } catch {
 328→                vaultSettingsLogger.error("Failed to regenerate phrase: \(error.localizedDescription, privacy: .public)")
 329→                await MainActor.run {
 330→                    regenerateErrorMessage = "Failed to regenerate recovery phrase. Please try again."
 331→                }
 332→            }
 333→        }
 334→    }
 335→    
 336→    private func loadVaultStatistics() {
 337→        guard let key = appState.currentVaultKey else { return }
 338→        
 339→        Task {
 340→            do {
 341→                let files = try VaultStorage.shared.listFiles(with: key)
 342→                let totalSize = files.reduce(0) { $0 + Int64($1.size) }
 343→                
 344→                // Check if this is the duress vault
 345→                let ownerFingerprint = KeyDerivation.keyFingerprint(from: key)
 346→                let duressInitiallyEnabled = await DuressHandler.shared.isDuressKey(key)
 347→
 348→                // Load sharing info
 349→                let index = try VaultStorage.shared.loadIndex(with: key)
 350→                let shared = index.isSharedVault ?? false
 351→                let shareCount = index.activeShares?.count ?? 0
 352→                let activeUploadJobs = await MainActor.run {
 353→                    ShareUploadManager.shared.jobs(forOwnerFingerprint: ownerFingerprint)
 354→                        .filter { $0.canTerminate }
 355→                }
 356→                let uploadCount = activeUploadJobs.count
 357→                let sharingDisablesDuress = Self.shouldDisableDuressForSharing(
 358→                    isSharedVault: shared,
 359→                    activeShareCount: shareCount,
 360→                    activeUploadCount: uploadCount
 361→                )
 362→                if duressInitiallyEnabled && sharingDisablesDuress {
 363→                    await DuressHandler.shared.clearDuressVault()
 364→                    vaultSettingsLogger.info("Cleared duress because this vault has active sharing")
 365→                }
 366→                let isDuress = duressInitiallyEnabled && !sharingDisablesDuress
 367→
 368→                await MainActor.run {
 369→                    fileCount = files.count
 370→                    storageUsed = totalSize
 371→                    isDuressVault = isDuress
 372→                    isSharedVault = shared
 373→                    activeShareCount = shareCount
 374→                    activeUploadCount = uploadCount
 375→                }
 376→            } catch {
 377→                vaultSettingsLogger.error("Failed to load vault statistics: \(error.localizedDescription, privacy: .public)")
 378→                await MainActor.run {
 379→                    fileCount = 0
 380→                    storageUsed = 0
 381→                    activeUploadCount = 0
 382→                }
 383→            }
 384→        }
 385→    }
 386→
 387→    static func shouldDisableDuressForSharing(
 388→        isSharedVault: Bool,
 389→        activeShareCount: Int,
 390→        activeUploadCount: Int
 391→    ) -> Bool {
 392→        isSharedVault || activeShareCount > 0 || activeUploadCount > 0
 393→    }
