 395→    private static let byteCountFormatter: ByteCountFormatter = {
 396→        let formatter = ByteCountFormatter()
 397→        formatter.countStyle = .file
 398→        formatter.allowedUnits = [.useBytes, .useKB, .useMB, .useGB]
 399→        return formatter
 400→    }()
 401→
 402→    private func formatBytes(_ bytes: Int64) -> String {
 403→        Self.byteCountFormatter.string(fromByteCount: bytes)
 404→    }
 405→}
