 407→// MARK: - Change Pattern
 408→
 409→enum ChangePatternStep: Equatable {
 410→    case verifyCurrent
 411→    case createNew
 412→    case confirmNew
 413→    case complete
 414→}
 415→
 416→struct ChangePatternFlowState {
 417→    var step: ChangePatternStep = .verifyCurrent
 418→    var currentPattern: [Int] = []
 419→    var newPattern: [Int] = []
 420→    var validationResult: PatternValidationResult?
 421→    var errorMessage: String?
 422→    var isProcessing = false
 423→    var newRecoveryPhrase = ""
 424→
 425→    mutating func clearFeedback() {
 426→        validationResult = nil
 427→        errorMessage = nil
 428→    }
 429→
 430→    mutating func showValidation(_ result: PatternValidationResult) {
 431→        validationResult = result
 432→        errorMessage = nil
 433→    }
 434→
 435→    mutating func showError(_ message: String) {
 436→        errorMessage = message
 437→        validationResult = nil
 438→    }
 439→
 440→    mutating func beginProcessingIfIdle() -> Bool {
 441→        guard !isProcessing else { return false }
 442→        isProcessing = true
 443→        return true
 444→    }
 445→
 446→    mutating func endProcessing() {
 447→        isProcessing = false
 448→    }
 449→
 450→    mutating func resetForStartOver() {
 451→        step = .verifyCurrent
 452→        currentPattern = []
 453→        newPattern = []
 454→        clearFeedback()
 455→        isProcessing = false
 456→    }
 457→
 458→    mutating func skipVerifyForTesting(pattern: [Int]) {
 459→        step = .createNew
 460→        currentPattern = pattern
 461→        newPattern = []
 462→        clearFeedback()
 463→        isProcessing = false
 464→    }
 465→
 466→    mutating func transitionToCreate(currentPattern pattern: [Int]) {
 467→        currentPattern = pattern
 468→        step = .createNew
 469→        clearFeedback()
 470→        isProcessing = false
 471→    }
 472→
 473→    mutating func transitionToConfirm(newPattern pattern: [Int]) {
 474→        newPattern = pattern
 475→        step = .confirmNew
 476→        clearFeedback()
 477→        isProcessing = false
 478→    }
 479→
 480→    mutating func complete(with recoveryPhrase: String) {
 481→        step = .complete
 482→        newRecoveryPhrase = recoveryPhrase
 483→        clearFeedback()
 484→        isProcessing = false
 485→    }
 486→}
