 817→    // MARK: - Actions
 818→
 819→    private func handlePatternComplete(_ pattern: [Int]) {
 820→        vaultSettingsLogger.debug("Pattern completed in \(String(describing: step), privacy: .public) step")
 821→
 822→        guard !isProcessing else {
 823→            vaultSettingsLogger.debug("Ignoring pattern input while processing")
 824→            return
 825→        }
 826→        
 827→        switch step {
 828→        case .verifyCurrent:
 829→            verifyCurrentPattern(pattern)
 830→            
 831→        case .createNew:
 832→            validateNewPattern(pattern)
 833→            
 834→        case .confirmNew:
 835→            confirmNewPattern(pattern)
 836→            
 837→        case .complete:
 838→            break
 839→        }
 840→    }
 841→    
 842→    private func verifyCurrentPattern(_ pattern: [Int]) {
 843→        guard let currentKey = appState.currentVaultKey else {
 844→            flow.showError("No vault key available")
 845→            patternState.reset()
 846→            return
 847→        }
 848→
 849→        // Validate pattern structure first
 850→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 851→        if !validation.isValid {
 852→            flow.showError(validation.errors.first?.message ?? "Invalid pattern")
 853→            patternState.reset()
 854→            return
 855→        }
 856→
 857→        guard flow.beginProcessingIfIdle() else { return }
 858→
 859→        Task {
 860→            do {
 861→                // Derive key from entered pattern and check if it matches current key
 862→                let enteredKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
 863→                
 864→                await MainActor.run {
 865→                    if enteredKey == currentKey {
 866→                        // Pattern verified - move to next step
 867→                        vaultSettingsLogger.debug("Current pattern verified")
 868→                        flow.transitionToCreate(currentPattern: pattern)
 869→                        patternState.reset()
 870→                    } else {
 871→                        // Pattern doesn't match
 872→                        vaultSettingsLogger.debug("Current pattern incorrect")
 873→                        flow.showError("Incorrect pattern. Please try again.")
 874→                        patternState.reset()
 875→                    }
 876→                    flow.endProcessing()
 877→                }
 878→            } catch {
 879→                await MainActor.run {
 880→                    flow.showError("Error verifying pattern: \(error.localizedDescription)")
 881→                    patternState.reset()
 882→                    flow.endProcessing()
 883→                }
 884→            }
 885→        }
 886→    }
 887→    
 888→    private func validateNewPattern(_ pattern: [Int]) {
 889→        guard flow.beginProcessingIfIdle() else { return }
 890→
 891→        Task {
 892→            // First, validate the pattern structure
 893→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 894→
 895→            if result.isValid {
 896→                // Pattern valid — don't show feedback yet (avoids brief flash before transition)
 897→                do {
 898→                    let newKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
 899→                    let hasFiles = VaultStorage.shared.vaultHasFiles(for: newKey)
 900→
 901→                    await MainActor.run {
 902→                        if hasFiles {
 903→                            vaultSettingsLogger.info("Pattern already used by a vault with files")
 904→                            flow.showError("This pattern is already used by another vault. Please choose a different pattern.")
 905→                            patternState.reset()
 906→                        } else {
 907→                            vaultSettingsLogger.debug("New pattern valid and unique")
 908→                            flow.transitionToConfirm(newPattern: pattern)
 909→                            patternState.reset()
 910→                        }
 911→                        flow.endProcessing()
 912→                    }
 913→                } catch {
 914→                    await MainActor.run {
 915→                        flow.showError("Error checking pattern: \(error.localizedDescription)")
 916→                        patternState.reset()
 917→                        flow.endProcessing()
 918→                    }
 919→                }
 920→            } else {
 921→                vaultSettingsLogger.debug("New pattern invalid")
 922→                await MainActor.run {
 923→                    flow.showValidation(result)
 924→                    patternState.reset()
 925→                    flow.endProcessing()
 926→                }
 927→            }
 928→        }
 929→    }
 930→    
 931→    private func confirmNewPattern(_ pattern: [Int]) {
 932→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 933→        if !validation.isValid {
 934→            flow.showError(validation.errors.first?.message ?? "Invalid pattern")
 935→            patternState.reset()
 936→            return
 937→        }
 938→
 939→        if pattern == newPattern {
 940→            vaultSettingsLogger.debug("Patterns match, updating vault")
 941→            updateVaultPattern(pattern)
 942→        } else {
 943→            vaultSettingsLogger.debug("Patterns don't match")
 944→            flow.showError("Patterns don't match. Please try again.")
 945→            patternState.reset()
 946→        }
 947→    }
 948→    
 949→    private func updateVaultPattern(_ newPattern: [Int]) {
 950→        guard let oldKey = appState.currentVaultKey else {
 951→            flow.showError("No vault key available")
 952→            return
 953→        }
 954→        
 955→        guard flow.beginProcessingIfIdle() else { return }
 956→        
 957→        Task {
 958→            do {
 959→                // 1. Derive new key from new pattern
 960→                let newKey = try await KeyDerivation.deriveKey(from: newPattern, gridSize: patternState.gridSize)
 961→                
 962→                vaultSettingsLogger.trace("New key derived")
 963→                
 964→                // 2. Change vault key (instant operation - only re-encrypts master key)
 965→                try VaultStorage.shared.changeVaultKey(from: oldKey, to: newKey)
 966→                
 967→                vaultSettingsLogger.debug("Vault key changed")
 968→                
 969→                // 3. Generate a new recovery phrase
 970→                let recoveryPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
 971→                
 972→                vaultSettingsLogger.trace("New recovery phrase generated")
 973→                
 974→                // 4. Save the new recovery phrase with the new key
 975→                try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
 976→                    phrase: recoveryPhrase,
 977→                    pattern: newPattern,
 978→                    gridSize: patternState.gridSize,
 979→                    patternKey: newKey
 980→                )
 981→                
 982→                vaultSettingsLogger.debug("Recovery phrase saved with new key")
 983→                
 984→                // 5. Delete old recovery data
 985→                try await RecoveryPhraseManager.shared.deleteRecoveryData(for: oldKey)
 986→                
 987→                vaultSettingsLogger.debug("Old recovery data deleted")
 988→                
 989→                EmbraceManager.shared.addBreadcrumb(category: "settings.patternChanged")
 990→
 991→                // 6. Update app state with new key
 992→                await MainActor.run {
 993→                    appState.currentVaultKey = newKey
 994→                    flow.complete(with: recoveryPhrase)
 995→
 996→                    vaultSettingsLogger.info("Pattern change complete")
 997→                }
 998→            } catch {
 999→                vaultSettingsLogger.error("Error updating pattern: \(error.localizedDescription, privacy: .public)")
1000→                await MainActor.run {
1001→                    flow.showError("Failed to update pattern: \(error.localizedDescription)")
1002→                    patternState.reset()
1003→                    flow.endProcessing()
1004→                }
1005→            }
1006→        }
1007→    }
1008→}
