1202→    private func saveCustomPhrase() {
1203→        guard let key = appState.currentVaultKey else {
1204→            errorMessage = "No vault key available"
1205→            return
1206→        }
1207→        
1208→        guard let validation = validation, validation.isAcceptable else {
1209→            return
1210→        }
1211→        
1212→        isProcessing = true
1213→        errorMessage = nil
1214→        
1215→        Task {
1216→            do {
1217→                let phrase = customPhrase.trimmingCharacters(in: .whitespacesAndNewlines)
1218→                do {
1219→                    _ = try await RecoveryPhraseManager.shared.regenerateRecoveryPhrase(
1220→                        for: key,
1221→                        customPhrase: phrase
1222→                    )
1223→                } catch RecoveryError.vaultNotFound {
1224→                    // Vault has no recovery data yet — create it
1225→                    guard let pattern = appState.currentPattern else {
1226→                        throw RecoveryError.vaultNotFound
1227→                    }
1228→                    try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
1229→                        phrase: phrase,
1230→                        pattern: pattern,
1231→                        gridSize: 5,
1232→                        patternKey: key
1233→                    )
1234→                }
1235→
1236→                await MainActor.run {
1237→                    isProcessing = false
1238→                    showSuccess = true
1239→                }
1240→            } catch RecoveryError.weakPhrase(let message) {
1241→                await MainActor.run {
1242→                    isProcessing = false
1243→                    errorMessage = message
1244→                }
1245→            } catch {
1246→                await MainActor.run {
1247→                    isProcessing = false
1248→                    errorMessage = "Failed to set custom phrase: \(error.localizedDescription)"
1249→                }
1250→            }
1251→        }
1252→    }
1253→}
