 324→    /// Performs a complete reset of all app data - DEBUG ONLY
 325→    private func debugFullReset() async {
 326→        settingsLogger.info("Starting full debug reset")
 327→        
 328→        // 1. Clear vault files and storage
 329→        await clearVaultStorage()
 330→        
 331→        // 2. Clear recovery phrase mappings
 332→        clearRecoveryMappings()
 333→        
 334→        // 3. Clear all UserDefaults
 335→        clearUserDefaults()
 336→        
 337→        // 4. Clear Keychain entries
 338→        clearKeychain()
 339→        
 340→        // 5. Clear temporary files
 341→        clearTemporaryFiles()
 342→        
 343→        // 6. Reset app state
 344→        await MainActor.run {
 345→            appState.lockVault()
 346→            appState.resetToOnboarding()
 347→        }
 348→        
 349→        settingsLogger.info("Full debug reset complete")
 350→    }
 351→    
 352→    private func clearVaultStorage() async {
 353→        // Delete vault storage directory
 354→        let fileManager = FileManager.default
 355→        let documentsURL = fileManager.urls(for: .documentDirectory, in: .userDomainMask)[0]
 356→        let vaultURL = documentsURL.appendingPathComponent("vault_storage")
 357→        
 358→        try? fileManager.removeItem(at: vaultURL)
 359→        
 360→        settingsLogger.debug("Vault storage cleared")
 361→    }
 362→    
 363→    private func clearRecoveryMappings() {
 364→        UserDefaults.standard.removeObject(forKey: "recovery_mapping")
 365→        
 366→        settingsLogger.debug("Recovery mappings cleared")
 367→    }
 368→    
 369→    private func clearUserDefaults() {
 370→        let domain = Bundle.main.bundleIdentifier!
 371→        UserDefaults.standard.removePersistentDomain(forName: domain)
 372→        UserDefaults.standard.synchronize()
 373→        
 374→        settingsLogger.debug("UserDefaults cleared")
 375→    }
 376→    
 377→    private func clearKeychain() {
 378→        // Clear all keychain items for the app
 379→        let secItemClasses = [
 380→            kSecClassGenericPassword,
 381→            kSecClassInternetPassword,
 382→            kSecClassCertificate,
 383→            kSecClassKey,
 384→            kSecClassIdentity
 385→        ]
 386→        
 387→        for itemClass in secItemClasses {
 388→            let spec: [String: Any] = [kSecClass as String: itemClass]
 389→            SecItemDelete(spec as CFDictionary)
 390→        }
 391→        
 392→        settingsLogger.debug("Keychain cleared")
 393→    }
 394→    
 395→    private func clearTemporaryFiles() {
 396→        let fileManager = FileManager.default
 397→        let tempURL = fileManager.temporaryDirectory
 398→        
 399→        // Remove all temporary files
 400→        if let contents = try? fileManager.contentsOfDirectory(at: tempURL, includingPropertiesForKeys: nil) {
 401→            for fileURL in contents {
 402→                try? fileManager.removeItem(at: fileURL)
 403→            }
 404→        }
 405→        
 406→        settingsLogger.debug("Temporary files cleared")
 407→    }
 408→    #endif
 409→}
