 575→struct iCloudBackupSettingsView: View {
 576→    @Environment(AppState.self) private var appState
 577→    @AppStorage("iCloudBackupEnabled") private var isBackupEnabled = false
 578→    @AppStorage("lastBackupTimestamp") private var lastBackupTimestamp: Double = 0
 579→    @State private var isBackingUp = false
 580→    @State private var backupStage: iCloudBackupManager.BackupStage?
 581→    @State private var uploadProgress: Double = 0
