 587→    /// Backups run automatically every 24 hours when enabled.
 588→    private static let backupInterval: TimeInterval = 24 * 60 * 60
 589→
 590→    private let backupManager = iCloudBackupManager.shared
 591→
 592→    private var lastBackupDate: Date? {
 593→        lastBackupTimestamp > 0 ? Date(timeIntervalSince1970: lastBackupTimestamp) : nil
 594→    }
 595→
 596→    private var nextBackupDate: Date? {
 597→        guard let last = lastBackupDate else { return nil }
 598→        return last.addingTimeInterval(Self.backupInterval)
 599→    }
 600→
 601→    private var isBackupOverdue: Bool {
 602→        guard let next = nextBackupDate else { return true }
 603→        return Date() >= next
 604→    }
