 767→    // MARK: - Logic
 768→
 769→    private func checkiCloudAvailable() async -> Bool {
 770→        let status = await CloudKitSharingManager.shared.checkiCloudStatus()
 771→        // .temporarilyUnavailable means signed in but CloudKit still syncing — treat as available
 772→        return status == .available || status == .temporarilyUnavailable
 773→    }
 774→
 775→    private func onAppear() {
 776→        Task { @MainActor in
 777→            let available = await checkiCloudAvailable()
 778→            iCloudAvailable = available
 779→            if !available && isBackupEnabled {
 780→                isBackupEnabled = false
 781→            }
 782→            // Auto-backup if enabled and overdue
 783→            if isBackupEnabled && available && isBackupOverdue {
 784→                performBackup()
 785→            }
 786→        }
 787→    }
 788→
 789→    private func handleToggleChange(_ enabled: Bool) {
 790→        guard enabled else {
 791→            backupTask?.cancel()
 792→            backupTask = nil
 793→            isBackingUp = false
 794→            backupStage = nil
 795→            UIApplication.shared.isIdleTimerDisabled = false
 796→            return
 797→        }
 798→
 799→        // Check iCloud via CloudKit account status before allowing enable
 800→        Task { @MainActor in
 801→            let available = await checkiCloudAvailable()
 802→            guard available else {
 803→                isBackupEnabled = false
 804→                iCloudAvailable = false
 805→                return
 806→            }
 807→            // First enable — run backup immediately
 808→            performBackup()
 809→        }
 810→    }
 811→
 812→    private func performBackup() {
 813→        guard !isBackingUp else { return }
 814→        guard let key = appState.currentVaultKey else {
 815→            errorMessage = "No vault key available"
 816→            return
 817→        }
 818→
 819→        isBackingUp = true
 820→        backupStage = nil
 821→        uploadProgress = 0
 822→        errorMessage = nil
 823→        UIApplication.shared.isIdleTimerDisabled = true
 824→
 825→        backupTask = Task {
 826→            defer {
 827→                Task { @MainActor in
 828→                    UIApplication.shared.isIdleTimerDisabled = false
 829→                }
 830→            }
 831→            do {
 832→                try await backupManager.performBackup(with: key, onProgress: { stage in
 833→                    Task { @MainActor in
 834→                        backupStage = stage
 835→                    }
 836→                }, onUploadProgress: { progress in
 837→                    Task { @MainActor in
 838→                        uploadProgress = progress
 839→                    }
 840→                })
 841→                await MainActor.run {
 842→                    lastBackupTimestamp = Date().timeIntervalSince1970
 843→                    isBackingUp = false
 844→                    backupStage = nil
 845→                }
 846→            } catch is CancellationError {
 847→                await MainActor.run {
 848→                    isBackingUp = false
 849→                    backupStage = nil
 850→                }
 851→            } catch iCloudError.notAvailable {
 852→                await MainActor.run {
 853→                    errorMessage = "iCloud is not available. Check that you're signed in to iCloud in Settings."
 854→                    isBackingUp = false
 855→                    backupStage = nil
 856→                }
 857→            } catch iCloudError.fileNotFound {
 858→                await MainActor.run {
 859→                    errorMessage = "No vault data found to back up. Add some files first."
 860→                    isBackingUp = false
 861→                    backupStage = nil
 862→                }
 863→            } catch {
 864→                EmbraceManager.shared.captureError(
 865→                    error,
 866→                    context: ["feature": "icloud_backup_settings"]
 867→                )
 868→                await MainActor.run {
 869→                    errorMessage = "\(error.localizedDescription)\n\nError type: \(type(of: error))\nDetails: \(error)"
 870→                    isBackingUp = false
 871→                    backupStage = nil
 872→                }
 873→            }
 874→        }
 875→    }
 876→}
