1063→    private func checkForBackup() async {
1064→        isCheckingBackup = true
1065→        let result = await backupManager.checkForBackup()
1066→        await MainActor.run {
1067→            switch result {
1068→            case .found(let info):
1069→                backupInfo = info
1070→                noBackupFound = false
1071→            case .notFound:
1072→                noBackupFound = true
1073→            case .error(let message):
1074→                noBackupFound = true
1075→                errorMessage = message
1076→            }
1077→            isCheckingBackup = false
1078→        }
1079→    }
