1081→    private func performRestore(with pattern: [Int]) {
1082→        guard pattern.count >= 6 else {
1083→            patternState.reset()
1084→            return
1085→        }
1086→
1087→        isRestoring = true
1088→        errorMessage = nil
1089→        restoreStage = "Deriving encryption key..."
1090→
1091→        Task {
1092→            do {
1093→                let key = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
1094→
1095→                await MainActor.run { restoreStage = "Downloading from iCloud..." }
1096→                try await backupManager.restoreBackup(with: key)
1097→
1098→                await MainActor.run {
1099→                    isRestoring = false
1100→                    restoreSuccess = true
1101→                }
1102→            } catch iCloudError.notAvailable {
1103→                await MainActor.run {
1104→                    errorMessage = "iCloud is not available. Check your connection and try again."
1105→                    isRestoring = false
1106→                    patternState.reset()
1107→                }
1108→            } catch iCloudError.downloadFailed {
1109→                await MainActor.run {
1110→                    errorMessage = "Decryption failed. This usually means the pattern doesn't match the one used to create the backup."
1111→                    isRestoring = false
1112→                    patternState.reset()
1113→                }
1114→            } catch {
1115→                await MainActor.run {
1116→                    errorMessage = "\(error.localizedDescription)\n\nDetails: \(error)"
1117→                    isRestoring = false
1118→                    patternState.reset()
1119→                }
1120→            }
1121→        }
1122→    }
1123→}
