 36→// MARK: - Date Grouping
 37→
 38→struct DateGroup: Identifiable {
 39→    let id: String // group title
 40→    let title: String
 41→    let items: [VaultFileItem] // all items in original order
 42→    let media: [VaultFileItem] // images + videos
 43→    let files: [VaultFileItem] // non-media files
 44→}
 45→
 46→func groupFilesByDate(_ items: [VaultFileItem], newestFirst: Bool = true) -> [DateGroup] {
 47→    let calendar = Calendar.current
 48→    let now = Date()
 49→    let startOfToday = calendar.startOfDay(for: now)
 50→    let startOfYesterday = calendar.date(byAdding: .day, value: -1, to: startOfToday)!
 51→
 52→    // Group items by calendar day
 53→    var dayBuckets: [(dayStart: Date, title: String, items: [VaultFileItem])] = []
 54→    var bucketIndex: [Date: Int] = [:]
 55→
 56→    let dayFormatter: DateFormatter = {
 57→        let f = DateFormatter()
 58→        f.doesRelativeDateFormatting = false
 59→        f.dateFormat = "EEEE, MMMM d"
 60→        return f
 61→    }()
 62→
 63→    for item in items {
 64→        let date = item.createdAt ?? .distantPast
 65→        let dayStart = calendar.startOfDay(for: date)
 66→
 67→        let title: String
 68→        if dayStart >= startOfToday {
 69→            title = "Today"
 70→        } else if dayStart >= startOfYesterday {
 71→            title = "Yesterday"
 72→        } else {
 73→            title = dayFormatter.string(from: date)
 74→        }
 75→
 76→        if let idx = bucketIndex[dayStart] {
 77→            dayBuckets[idx].items.append(item)
 78→        } else {
 79→            bucketIndex[dayStart] = dayBuckets.count
 80→            dayBuckets.append((dayStart: dayStart, title: title, items: [item]))
 81→        }
 82→    }
 83→
 84→    // Sort buckets to match the chosen sort direction
 85→    dayBuckets.sort { newestFirst ? $0.dayStart > $1.dayStart : $0.dayStart < $1.dayStart }
 86→
 87→    let isoFormatter = ISO8601DateFormatter()
 88→    return dayBuckets.map { bucket in
 89→        let media = bucket.items.filter { $0.isMedia }
 90→        let files = bucket.items.filter { !$0.isMedia }
 91→        let uniqueId = isoFormatter.string(from: bucket.dayStart)
 92→        return DateGroup(id: uniqueId, title: bucket.title, items: bucket.items, media: media, files: files)
 93→    }
 94→}
