149→    func computeVisibleFiles() -> VisibleFiles {
150→        var visible = files
151→        switch fileFilter {
152→        case .all:
153→            break
154→        case .media:
155→            visible = visible.filter {
156→                let mime = $0.mimeType ?? ""
157→                return mime.hasPrefix("image/") || mime.hasPrefix("video/")
158→            }
159→        case .documents:
160→            visible = visible.filter {
161→                let mime = $0.mimeType ?? ""
162→                return !mime.hasPrefix("image/") && !mime.hasPrefix("video/")
163→            }
164→        }
165→        if !searchText.isEmpty {
166→            visible = visible.filter {
167→                ($0.filename ?? "").localizedStandardContains(searchText) ||
168→                ($0.mimeType ?? "").localizedStandardContains(searchText)
169→            }
170→        }
171→        switch sortOrder {
172→        case .dateNewest:
173→            visible.sort { ($0.createdAt ?? .distantPast) > ($1.createdAt ?? .distantPast) }
174→        case .dateOldest:
175→            visible.sort { ($0.createdAt ?? .distantPast) < ($1.createdAt ?? .distantPast) }
176→        case .sizeSmallest:
177→            visible.sort { $0.size < $1.size }
178→        case .sizeLargest:
179→            visible.sort { $0.size > $1.size }
180→        case .name:
181→            visible.sort { ($0.filename ?? "").localizedStandardCompare($1.filename ?? "") == .orderedAscending }
182→        }
183→
184→        let media = visible.filter { $0.isMedia }
185→        let documents = visible.filter { !$0.isMedia }
186→        let mediaIndexById = Dictionary(
187→            uniqueKeysWithValues: media.enumerated().map { ($1.id, $0) }
188→        )
189→        return VisibleFiles(
190→            all: visible,
191→            media: media,
192→            documents: documents,
193→            mediaIndexById: mediaIndexById
194→        )
195→    }
196→
197→    var sortedFiles: [VaultFileItem] {
198→        computeVisibleFiles().all
199→    }
200→
201→    var splitFiles: (all: [VaultFileItem], media: [VaultFileItem], documents: [VaultFileItem]) {
202→        let visible = computeVisibleFiles()
203→        return (visible.all, visible.media, visible.documents)
204→    }
