463→struct VaultFileItem: Identifiable, Sendable {
464→    let id: UUID
465→    let size: Int
466→    let encryptedThumbnail: Data?
467→    let mimeType: String?
468→    let filename: String?
469→    let createdAt: Date?
470→    let duration: TimeInterval?
471→
472→    init(id: UUID, size: Int, encryptedThumbnail: Data?, mimeType: String?, filename: String?, createdAt: Date? = nil, duration: TimeInterval? = nil) {
473→        self.id = id
474→        self.size = size
475→        self.encryptedThumbnail = encryptedThumbnail
476→        self.mimeType = mimeType
477→        self.filename = filename
478→        self.createdAt = createdAt
479→        self.duration = duration
480→    }
481→
482→    var isImage: Bool {
483→        (mimeType ?? "").hasPrefix("image/")
484→    }
485→
486→    var isMedia: Bool {
487→        let mime = mimeType ?? ""
488→        return mime.hasPrefix("image/") || mime.hasPrefix("video/")
489→    }
490→}
