  1→import SwiftUI
  2→import AVKit
  3→
  4→struct FullScreenPhotoViewer: View {
  5→    let files: [VaultFileItem]
  6→    let vaultKey: Data?
  7→    let masterKey: Data?
  8→    let initialIndex: Int
  9→    var onDelete: ((UUID) -> Void)?
 10→    var allowDownloads: Bool = true
 11→
 12→    @Environment(\.dismiss) private var dismiss
 13→    @State private var currentIndex: Int
 14→    @State private var pageScrollID: Int?
 15→    @State private var images: [UUID: UIImage] = [:]
 16→    @State private var showingActions = false
 17→    @State private var showingExportConfirmation = false
 18→    @State private var showingDeleteConfirmation = false
 19→    @State private var shareURL: URL?
 20→    @State private var showingVideoPlayer: VaultFileItem?
 21→    @State private var verticalDismissOffset: CGFloat = 0
 22→
 23→    init(files: [VaultFileItem], vaultKey: Data?, masterKey: Data? = nil, initialIndex: Int,
 24→         onDelete: ((UUID) -> Void)? = nil, allowDownloads: Bool = true) {
 25→        self.files = files
 26→        self.vaultKey = vaultKey
 27→        self.masterKey = masterKey
 28→        self.initialIndex = initialIndex
 29→        self.onDelete = onDelete
 30→        self.allowDownloads = allowDownloads
 31→        self._currentIndex = State(initialValue: initialIndex)
 32→    }
 33→
 34→    private var currentFile: VaultFileItem? {
 35→        guard files.indices.contains(currentIndex) else { return nil }
 36→        return files[currentIndex]
 37→    }
 38→
 39→    private var backgroundOpacity: Double {
 40→        let progress = min(max(verticalDismissOffset / 300, 0), 1)
 41→        return max(0.45, 1 - Double(progress) * 0.55)
 42→    }
 43→
 44→    var body: some View {
 45→        GeometryReader { geometry in
 46→            ZStack {
 47→                Color.vaultBackground
 48→                    .opacity(backgroundOpacity)
 49→                    .ignoresSafeArea()
 50→
 51→                ScrollView(.horizontal) {
 52→                    LazyHStack(spacing: 0) {
 53→                        ForEach(Array(files.enumerated()), id: \.element.id) { index, file in
 54→                            photoPage(file: file)
 55→                                .frame(width: geometry.size.width, height: geometry.size.height)
 56→                                .background(Color.vaultBackground)
 57→                                .clipped()
 58→                                .id(index)
 59→                        }
 60→                    }
 61→                    .scrollTargetLayout()
 62→                }
 63→                .scrollIndicators(.hidden)
 64→                .scrollTargetBehavior(.paging)
 65→                .scrollPosition(id: $pageScrollID)
 66→                .onAppear {
 67→                    pageScrollID = currentIndex
 68→                }
 69→                .onChange(of: pageScrollID) { _, newID in
 70→                    guard let newID else { return }
 71→                    if currentIndex != newID {
 72→                        currentIndex = newID
 73→                    }
 74→                }
 75→                .onChange(of: currentIndex) { _, newIndex in
 76→                    if pageScrollID != newIndex {
 77→                        pageScrollID = newIndex
 78→                    }
 79→                    preloadAdjacent(around: newIndex)
 80→                    evictDistant(from: newIndex)
 81→                }
 82→                .offset(y: verticalDismissOffset)
 83→                // Top bar overlay
 84→                VStack {
 85→                    HStack {
 86→                        Button("Done") { dismiss() }
 87→                            .frame(minWidth: 44, minHeight: 44)
 88→                            .accessibilityIdentifier("viewer_done")
 89→                        Spacer()
 90→                        if onDelete != nil || allowDownloads {
 91→                            Button(action: { showingActions = true }) {
 92→                                Image(systemName: "ellipsis.circle")
 93→                                    .imageScale(.large)
 94→                            }
 95→                            .frame(minWidth: 44, minHeight: 44)
 96→                            .accessibilityIdentifier("viewer_actions")
 97→                            .accessibilityLabel("More actions")
 98→                        }
 99→                    }
100→                    .padding(.horizontal)
101→                    .padding(.vertical, 12)
102→                    Spacer()
103→                }
104→                .offset(y: verticalDismissOffset)
105→            }
106→            .simultaneousGesture(dragToDismissGesture)
107→        }
108→        .task {
109→            preloadAdjacent(around: initialIndex)
110→        }
111→        .confirmationDialog("Actions", isPresented: $showingActions, titleVisibility: .visible) {
112→            if allowDownloads {
113→                Button("Export") { showingExportConfirmation = true }
114→            }
115→            if onDelete != nil {
116→                Button("Delete", role: .destructive) { showingDeleteConfirmation = true }
117→            }
118→            Button("Cancel", role: .cancel) { /* No-op */ }
119→        }
120→        .alert("Export File?", isPresented: $showingExportConfirmation) {
121→            Button("Export") { exportFile() }
122→            Button("Cancel", role: .cancel) { /* No-op */ }
123→        } message: {
124→            Text("This will save the file outside the vault. It will no longer be protected.")
125→        }
126→        .alert("Delete File?", isPresented: $showingDeleteConfirmation) {
127→            Button("Delete", role: .destructive) { deleteFile() }
128→            Button("Cancel", role: .cancel) { /* No-op */ }
129→        } message: {
130→            Text("This file will be permanently deleted from the vault.")
131→        }
132→        .onChange(of: shareURL) { _, url in
133→            guard let url else { return }
134→            ShareSheetHelper.present(items: [url]) {
135→                try? FileManager.default.removeItem(at: url)
136→                self.shareURL = nil
137→            }
138→        }
139→        .fullScreenCover(item: $showingVideoPlayer) { file in
140→            SecureVideoPlayer(file: file, vaultKey: vaultKey)
141→        }
142→    }
143→
144→    private var dragToDismissGesture: some Gesture {
145→        DragGesture(minimumDistance: 16)
146→            .onChanged { value in
147→                let horizontal = abs(value.translation.width)
148→                let vertical = value.translation.height
149→                guard vertical > 0 else { return }
150→                guard abs(vertical) > horizontal * 1.15 else { return }
151→                verticalDismissOffset = vertical
152→            }
153→            .onEnded { value in
154→                let horizontal = abs(value.translation.width)
155→                let vertical = value.translation.height
156→                let predictedVertical = value.predictedEndTranslation.height
157→                let shouldDismiss = vertical > 90 && abs(vertical) > horizontal * 1.15
158→                    || predictedVertical > 160
159→
160→                if shouldDismiss {
161→                    dismiss()
162→                } else {
163→                    withAnimation(.spring(response: 0.3, dampingFraction: 0.82)) {
164→                        verticalDismissOffset = 0
165→                    }
166→                }
167→            }
168→    }
169→
170→    // MARK: - Page Content
171→
172→    @ViewBuilder
173→    private func photoPage(file: VaultFileItem) -> some View {
174→        let isVideo = (file.mimeType ?? "").hasPrefix("video/")
175→
176→        if let image = images[file.id] {
177→            ZStack {
178→                Image(uiImage: image)
179→                    .resizable()
180→                    .scaledToFit()
181→                    .containerRelativeFrame([.horizontal, .vertical])
182→
183→                if isVideo {
184→                    Button {
185→                        showingVideoPlayer = file
186→                    } label: {
187→                        Image(systemName: "play.circle.fill")
188→                            .font(.system(size: 72))
189→                            .foregroundStyle(.vaultSecondaryText)
190→                            .shadow(radius: 4)
191→                    }
192→                    .buttonStyle(.plain)
193→                    .accessibilityIdentifier("viewer_play_video")
194→                }
195→            }
196→        } else {
197→            ProgressView()
198→                .containerRelativeFrame([.horizontal, .vertical])
199→                .task(id: file.id) {
200→                    await loadFullImage(for: file)
201→                }
202→        }
203→    }
204→
205→    // MARK: - Image Loading
206→
207→    private func loadFullImage(for file: VaultFileItem) async {
208→        guard images[file.id] == nil else { return }
209→
210→        let isVideo = (file.mimeType ?? "").hasPrefix("video/")
211→
212→        if isVideo {
213→            // For videos, decrypt the thumbnail from the file entry (fast, ~30KB)
214→            guard let masterKey = masterKey,
215→                  let encryptedThumb = file.encryptedThumbnail else { return }
216→            do {
217→                let thumbData = try CryptoEngine.decrypt(encryptedThumb, with: masterKey)
218→                guard let uiImage = UIImage(data: thumbData) else { return }
219→                await MainActor.run {
220→                    images[file.id] = uiImage
221→                }
222→            } catch {
223→                // Thumbnail decryption failed
224→            }
225→        } else {
226→            // For images, load full content
227→            guard let key = vaultKey else { return }
228→            do {
229→                let (header, content) = try VaultStorage.shared.retrieveFile(id: file.id, with: key)
230→                guard header.mimeType.hasPrefix("image/"),
231→                      let uiImage = UIImage(data: content) else { return }
232→                await MainActor.run {
233→                    images[file.id] = uiImage
234→                }
235→            } catch {
236→                // Loading failed — cell stays as spinner
237→            }
238→        }
239→    }
240→
241→    private func preloadAdjacent(around index: Int) {
242→        let range = max(0, index - 1)...min(files.count - 1, index + 1)
243→        for i in range {
244→            let file = files[i]
245→            if images[file.id] == nil {
246→                Task { await loadFullImage(for: file) }
247→            }
248→        }
249→    }
250→
251→    private func evictDistant(from index: Int) {
252→        for (i, file) in files.enumerated() {
253→            if abs(i - index) > 2 {
254→                images.removeValue(forKey: file.id)
255→            }
256→        }
257→    }
258→
259→    // MARK: - Actions
260→
261→    private func exportFile() {
262→        guard let file = currentFile,
263→              let image = images[file.id],
264→              let data = image.jpegData(compressionQuality: 0.95) else { return }
265→
266→        let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
267→        let filename = "Export_\(file.id.uuidString).jpg"
268→        let url = tempDir.appendingPathComponent(filename)
269→
270→        do {
271→            try data.write(to: url, options: [.atomic])
272→            shareURL = url
273→        } catch {
274→            // Ignore
275→        }
276→    }
277→
278→    private func deleteFile() {
279→        guard let file = currentFile, let key = vaultKey else { return }
280→
281→        Task {
282→            do {
283→                try VaultStorage.shared.deleteFile(id: file.id, with: key)
284→            } catch {
285→                EmbraceManager.shared.captureError(error)
286→            }
287→            await MainActor.run {
288→                onDelete?(file.id)
289→                dismiss()
290→            }
291→        }
292→    }
293→}
