 19→                if !files.isEmpty && !subscriptionManager.isPremium {
 20→                    StorageRingView(
 21→                        fileCount: files.count,
 22→                        maxFiles: SubscriptionManager.maxFreeFilesPerVault,
 23→                        totalBytes: Int64(files.reduce(0) { $0 + $1.size })
 24→                    )
 25→                }
 26→
 27→                Button(action: lockVault) {
 28→                    Image(systemName: "lock.fill")
 29→                }
 30→                .accessibilityIdentifier("vault_lock_button")
 31→                .accessibilityLabel("Lock vault")
 32→            }
 33→        }
 34→    }
