 36→    // MARK: - Top Safe Area Inset
 37→
 38→    var topSafeAreaContent: some View {
 39→        VStack(spacing: 8) {
 40→            if !files.isEmpty && !showingSettings {
 41→                HStack(spacing: 8) {
