 65→    // MARK: - Edit Mode Controls
 66→
 67→    private var editModeControls: some View {
 68→        Group {
 69→            // Select All / Deselect All button
 70→            Button {
 71→                withAnimation(.easeInOut(duration: 0.2)) {
 72→                    if selectedIds.count == sortedFiles.count {
 73→                        selectedIds.removeAll()
 74→                    } else {
 75→                        selectedIds = Set(sortedFiles.map(\.id))
 76→                    }
 77→                }
 78→            } label: {
 79→                Text(selectedIds.count == sortedFiles.count ? "Deselect All" : "Select All (\(sortedFiles.count))")
 80→                    .font(.subheadline.weight(.medium))
 81→                    .lineLimit(1)
 82→                    .padding(.horizontal, 12)
 83→                    .padding(.vertical, 10)
 84→                    .frame(maxWidth: .infinity)
 85→                    .vaultGlassBackground(cornerRadius: 12)
 86→            }
 87→            .accessibilityIdentifier("vault_select_all")
 88→
 89→            // Done button
 90→            Button {
 91→                withAnimation(.easeInOut(duration: 0.2)) {
 92→                    isEditing = false
 93→                    selectedIds.removeAll()
 94→                }
 95→            } label: {
 96→                Text("Done")
 97→                    .font(.subheadline.weight(.semibold))
 98→                    .padding(.horizontal, 12)
 99→                    .padding(.vertical, 10)
100→                    .vaultGlassBackground(cornerRadius: 12)
101→            }
102→            .accessibilityIdentifier("vault_edit_done")
103→        }
104→    }
